00515 void Burnup::writeMaterials()
00516 {
00517     std::string fileName = "Evolved_Materials_Step" + std::to_string(m_currentStep);
00518     std::ofstream file;
00519     file.open(fileName, std::ios::out);
00520 
00521     file << "// ****************************************************"                    << std::endl;
00522     file << "// ********** BEAM PARAMETERS FOR ACTIVATION **********"                    << std::endl;
00523     file << "// ****************************************************\n"                  << std::endl;
00524 
00525     file << "// * Core power:                " << m_corePower << " MW"                   << std::endl;
00526     file << "// * Beam power:                " << m_beamPower << " MW"                   << std::endl;
00527     file << "// * Beam energy:               " << m_beamEnergy << " MeV"                 << std::endl;
00528     file << "// * Beam intensity:            " << m_beamIntensity * 1000 << " mA"        << std::endl;
00529     file << "// * Step irradiation period:   " << m_currentIrradiationPeriod << " s " << " ("
00530               << m_currentIrradiationPeriod / m_totalIrradiationPeriod * 100 << " %)"      << std::endl;
00531     file << "// * Step scaling factor:       " << m_scalingFactor
00532               << " number of primaries during step irradiation period"                     << std::endl;
00533     file << "// * Current irradiation time:  " << m_irradiationTimePassed << " s "
00534               << " (" << m_irradiationTimePassed / m_totalIrradiationPeriod * 100
00535                                                                                  << " %)"  << std::endl;
00536 
00537     for(const auto& mat : m_matToLV)
00538     {
00539         TMXMaterial* newMat = (TMXMaterial*)TMXMaterial::GetMaterial(getMatNameCurrentStep(mat.first), true);
00540         if(!newMat) newMat = mat.first;
00541 
00542         file              << "\n// Material: "       <<newMat->GetName()
00543                           << " appearing in "        << m_matToLV[mat.first].size()
00544                           << " logical volume(s):"   <<std::endl;
00545 
00546         G4double totalVolume = 0.;
00547         G4double totalMass   = 0.;
00548         for(std::vector<G4LogicalVolume*>::size_type i=0; i<mat.second.size(); i++)
00549         {
00550             G4double daugtherVolume = 0.;
00551             for(size_t j = 0; j < mat.second[i]->GetNoDaughters(); j++)
00552             {
00553                 daugtherVolume += mat.second[i]->GetDaughter(j)->GetLogicalVolume()->GetSolid()->GetCubicVolume() / cm3;
00554             }
00555             totalVolume += mat.second[i]->GetSolid()->GetCubicVolume() / cm3 - daugtherVolume;
00556             G4double addMass = mat.second[i]->GetMass(false, false) / g;
00557             totalMass   += addMass;
00558             file          << "// LogicalVolume: "   << mat.second[i]->GetName()
00559                           << " with mass: "         << addMass
00560                           << " g and volume: "      << mat.second[i]->GetSolid()->GetCubicVolume() / cm3 - daugtherVolume
00561                           << " cm3"                 << std::endl;
00562         }
00563         file              << std::endl;
00564 
00565         // int count = 0;
00566         const G4double* massFraction    = newMat->GetFractionVector();
00567         std::string matName = newMat->GetName();
00568         std::replace(matName.begin(), matName.end(), '*', '_');
00569         file              << "TMXMaterial* "       << matName
00570                           << " = new TMXMaterial(\"" << newMat->GetName()
00571                           << "\", "                 << totalMass / totalVolume
00572                           << " * (g / cm3), "       << newMat->GetElementVector()->size()
00573                           << ", "                   << m_State[newMat->GetState()]
00574                           << ", "                   << newMat->GetTemperature()
00575                           << ", "                   << newMat->GetPressure()
00576                           << ");"                   << std::endl;
00577 
00578         for(unsigned int j=0; j < newMat->GetNumberOfElements(); ++j)
00579         {
00580             //Loop on the number of isotopes of the element
00581             for(unsigned int i = 0; i <  newMat->GetElement(j)->GetNumberOfIsotopes(); i++)
00582             {
00583                 double IZA = newMat->GetElement(j)->GetZ() * 10000 + newMat->GetElement(j)->GetIsotope(0)->GetN() * 10 + newMat->GetElement(j)->GetIsotope(0)->Getm();
00584 
00585                 file      << matName
00586                           << "->AddElement("        << IZA
00587                           << ", "                   << massFraction[j]
00588                           << ");"                   << std::endl;
00589             }
00590         }
00591         file              << "\n"                   << std::endl;
00592     }
00593     file.close();
00594 }
