00600 void Burnup::printMaterial(TMXMaterial* mat)
00601 {
00602     TMXMaterial* matSubstitute = mat;
00603     std::string initialString = "Material: ";
00604     std::string finalString   = "";
00605 
00606     if(mat->GetName().find("*") == std::string::npos)
00607         initialString = "Modifying: ";
00608     else
00609     {
00610         int pos = mat->GetName().find("*");
00611         std::string substring = mat->GetName().substr(pos + 1, mat->GetName().length());
00612 
00613         if(std::stoi(substring) == m_currentStep)
00614         {
00615             initialString = "By: ";
00616             finalString   = "\n-----------------------------------------------------------------------------------\n";
00617             matSubstitute = (TMXMaterial*)TMXMaterial::GetMaterial(getMatName(mat, m_currentStep - 1), true);
00618         }
00619         else if(std::stoi(substring) == m_currentStep - 1 || substring.length() == 0)
00620             initialString = "Modifying: ";
00621     }
00622 
00623     std::cout         << initialString         << mat->GetName()
00624                       << " appearing in "      << m_matToLV[matSubstitute].size()
00625                       << " logical volume(s):" <<std::endl;
00626 
00627     G4double totalVolume = 0.;
00628     G4double totalMass   = 0.;
00629     for(std::vector<G4LogicalVolume*>::size_type i=0; i<m_matToLV[matSubstitute].size(); i++)
00630     {
00631         G4double daugtherVolume = 0.;
00632         for(size_t j = 0; j < m_matToLV[matSubstitute][i]->GetNoDaughters(); j++)
00633         {
00634             daugtherVolume += m_matToLV[matSubstitute][i]->GetDaughter(j)->GetLogicalVolume()->GetSolid()->GetCubicVolume() / cm3;
00635         }
00636         totalVolume += m_matToLV[matSubstitute][i]->GetSolid()->GetCubicVolume() / cm3 - daugtherVolume;
00637         G4double addMass = m_matToLV[matSubstitute][i]->GetMass(false, false) / g;
00638         totalMass   += addMass;
00639         std::cout     << "\tLogicalVolume: "  << m_matToLV[matSubstitute][i]->GetName()
00640                       << " with mass: "       << addMass
00641                       << " g and volume: "    << m_matToLV[matSubstitute][i]->GetSolid()->GetCubicVolume() / cm3 - daugtherVolume
00642                       << " cm3"               << std::endl;
00643     }
00644 
00645     std::cout         << "\tTotal mass: "     << totalMass
00646                       << " ,Total volume: "   << totalVolume
00647                       << " cm3, Density: "    << totalMass / totalVolume
00648                       << " g / cm3"           << std::endl;
00649 
00650     const G4double* massFraction    = mat->GetFractionVector();
00651     for(unsigned int j=0; j < mat->GetNumberOfElements(); ++j)
00652     {
00653         G4double* relativeAbundance = mat->GetElement(j)->GetRelativeAbundanceVector();
00654         std::cout     << "\t\tElement: "      << mat->GetElement(j)->GetName()
00655                       << " "                  << massFraction[j]
00656                       << " "                  << mat->GetElement(j)->GetA() / (g/mole)
00657                       << std::endl;
00658 
00659         //Loop on the number of isotopes of the element
00660         for(unsigned int i = 0; i <  mat->GetElement(j)->GetNumberOfIsotopes(); i++)
00661         {
00662             const G4Isotope* Isotope = mat->GetElement(j)->GetIsotope(i);
00663             G4double nAtoms =totalMass * CLHEP::Avogadro / ( mat->GetElement(j)->GetA() / (g/mole) ) * relativeAbundance[i] * massFraction[j];
00664             std::cout << "\t\t\tIsotope: "    << Isotope->GetName()
00665                       << " "                  << nAtoms
00666                       << " "                  << relativeAbundance[i]
00667                       << " "                  << Isotope->GetA()/(g/mole)
00668                       << std::endl;
00669         }
00670     }
00671     std::cout<<endl;   std::cout << finalString << std::endl;
00672 }
