00089 void EventAction::BeginOfEventAction(const G4Event* /*evt*/)
00090 {
00091     // std::cout << "Begin of BeginOfEventAction" << std::endl;
00092   TMXStopTheRun::getInstance()->UserBeginOfEventAction();
00093 
00094 // Increase number of events
00095   ++fnEvt;
00096   fEdep = 0;
00097   fNbFission = 0;
00098   fEperFission = 0;
00099   fNb_EperFission = 0;
00100 
00101   // beginning of a new cycle -> Reset of keff estimators
00102   fKeff_collision = 0;
00103   fKeff_analogAbs = 0;
00104   fKeff_implicitAbs = 0;
00105   fKeff_trackLength = 0;
00106   fNsourceNeutronPerEvent = 0;
00107   fVec_pos.clear();
00108   fTimer.Start();
00109 
00110   // Initialize user actions
00111   if ( fnEvt%fInputParam->PrintModulo == 0 )
00112   {
00113       Run* run = static_cast<Run*>(G4RunManager::GetRunManager()->GetNonConstCurrentRun());
00114       if( fRunNumber != run->GetRunNumber() ) {
00115           fRunNumber  = run->GetRunNumber();
00116           Initialize( run->GetRunNumber(), fInputParam, *fRadiusAnglesHeight, *fmergedOutput, *fbatemanDecay);
00117       }
00118       G4cout << "EventAction: Event # " << fnEvt << " started" << G4endl;
00119       if(fnEvt > 0) {
00120         G4cout << "Time per event " << fTotalTime / fnEvt << " seconds" << G4endl;
00121         if(fInputParam->nThreads > 0)
00122             G4cout << "Finishing in " << (fInputParam->Nparticles - run->GetNumberOfEvent()) / fInputParam->nThreads * fTotalTime / fnEvt / 3600. << " hours" << G4endl;
00123       }
00124   }
00125 
00126   CalculationRelAbundU235_238();
00127 //   std::cout << "End of BeginOfEventAction" << std::endl;
00128 }
00129 
00131 // At the end of the event
00132 void EventAction::EndOfEventAction( const G4Event* /*evt*/ )
00133 {
00134 //     std::cout << "Begin of EndOfEventAction" << std::endl;
00135 
00136     RunAction::Instance()->AddEtotDeposit(fEdep);
00137 
00138   if(TMXOption::IsKeffCalculation(fInputParam->int_Option) /*&& fnEvt<3e4*/) {
00139       UpdateSpaceDistribution();
00140   }  // if dedicated keff calculation
00141 
00142   // Update MCNP keff estimators at each cycle end
00143   if(fNsourceNeutronPerEvent>0) {
00144       neutronicParam->UpdateKeffEstimators(fKeff_collision, fKeff_analogAbs, fKeff_implicitAbs, fKeff_trackLength, fNsourceNeutronPerEvent);
00145   }
00146 
00147   // Do the calculation of neutronic parameters of interest
00148   neutronicParam->DoCalculationAndPrintValues(fN0abs, fFissionN, fInelasticN, fN_AbsU5, fRelativeAbundanceU235,  fN_CapU8, fN_absTot, fRelativeAbundanceU238, fEdep, fEbeam, fNbFission, fEperFission, fNb_EperFission, fnu, fN0fission, *fRadiusAnglesHeight, fnEvt, fNbNperGeneration, fNf);
00149 
00150   //std::cout << "Burn-up calculation" << std::endl;
00151   // Burn-up calculation
00152 //  G4AutoLock lock(&myHEPPrimGenMutex);  // if G4MULTITHREADED mode activated, it is necessary to lock other threads before Writing in the shared class
00153 //  fbatemanDecay->AddTransmutedElement(vectorTransmutedElement);  // Add Transmuted Elements to the global class
00154 //  fbatemanDecay->AddTransmutedSrcElem(vectorSourceElement);  // Add Transmuted Elements considered as source to the global class
00155 //  vectorTransmutedElement.clear();
00156 //  vectorSourceElement.clear();
00157 
00158   // Stop timer to estimate the time per event
00159   fTimer.Stop();
00160   fTotalTime += fTimer.GetRealElapsed();
00161 
00162   // if the user press ctrl+c the calculation is stopped properly
00163   TMXStopTheRun::getInstance()->UserEndOfEventAction();
00164 
00165 //   std::cout << "End of EndOfEventAction" << std::endl;
00166 }
