00170 void EventAction::AddWeight(G4double X, G4double Y, G4double Z) {
00171     std::vector<double> pos{X, Y, Z};
00172     fVec_pos.push_back(pos);
00173 }
00174 
00175 void EventAction::UpdateSpaceDistribution() {
00176     // Change the distribution to converge to eigenstate
00177     G4AutoLock lock(&myHEPPrimGenMutex);
00178     fRadiusAnglesHeight->AddBinWeights(fVec_pos);
00179 }
