00181 void EventAction::Initialize(G4int RunNumber, TMXInputParameters* parameter, TMXRandomDistribution3D& RadiusAnglesHeight, TMXMergeThreadsResults& mergedOutput, TMXBatemanDecay& batemanDecay) {
00182 //   std::cout << "EventAction Initialization" << std::endl;
00183 
00184   fUI = G4UImanager::GetUIpointer();
00185   fInputParam = parameter;
00186 
00187   #ifdef G4MULTITHREADED
00188     int threadID = G4Threading::G4GetThreadId();
00189   #else
00190     int threadID = 0;
00191   #endif
00192 
00193   if(neutronicParam)
00194       neutronicParam->Initialize(RunNumber, mergedOutput, fInputParam, threadID);
00195   else
00196       neutronicParam = new TMXNeutronicParam(RunNumber, mergedOutput, fInputParam, threadID);
00197 
00198   fmergedOutput = &mergedOutput;
00199   fRunNumber = RunNumber;
00200   fnEvt = 0;
00201   fTotalTime = 0;
00202   fbool_First = true;
00203   fNbFission = 0;
00204   fNf = 0;
00205   fN0abs = 0;
00206   fN0fission = 0;
00207   fInelasticN = 0;
00208   fFissionN = 0;
00209   fNbNperGeneration.clear();
00210   fnu = 0;
00211   fRelativeAbundanceU235 = 1;
00212   fRelativeAbundanceU238 = 1;
00213   fN_AbsU5 = 0;
00214   fN_CapU8 = 0;
00215   fN_absTot= 0;
00216   fEdep = 0;
00217   fEperFission = 0;
00218   fNb_EperFission = 0;
00219   fN_primaryEscape = 0;
00220   if (!fInputParam->ExtSourcePath.empty())
00221       fEbeam = 0;
00222   else
00223       fEbeam = static_cast<const PrimaryGeneratorAction*> (G4RunManager::GetRunManager()->GetUserPrimaryGeneratorAction())->GetBeamEnergy()/eV; // eV
00224 
00225   // Random sampling of source neutron position for keff Calculation (fInputParam->int_Option = 4)
00226   fVec_pos.clear();
00227   fRadiusAnglesHeight = &RadiusAnglesHeight;
00228 
00229   // Calculation of burn-up
00230   fbatemanDecay = &batemanDecay;
00231   vectorTransmutedElement.clear();
00232   vectorSourceElement.clear();
00233 
00234   fEdep_fission = 0;
00235   fEkin_sec_fission = 0;
00236   fNtot_fission = 0;
00237 }
