00239 void EventAction::CalculationRelAbundU235_238() {// Calculation of the relative abundance of U-235 and U-238 in the fuel to calculate the ratio between capture on U-238 and fission on U-235 (quality of the neutron energy spectrum)
00240   if(fbool_First) {
00241       // Relative abundance of U-235 and U-238 from the geometry
00242       G4VPhysicalVolume* Fuel = G4PhysicalVolumeStore::GetInstance()->GetVolume("Fuel",false); // WARNING the name of the physical volume is hard coded
00243       if(Fuel) {
00244         G4Material* BreederMaterial = Fuel->GetLogicalVolume()->GetMaterial();
00245         for(long unsigned int j=0; j < BreederMaterial->GetNumberOfElements(); ++j) {
00246             const G4Element* Element = BreederMaterial->GetElement(j);
00247             if(Element->GetName() == "TS_U_of_Uranium_Dioxide") {
00248 //             if(Element->GetName() == "enriched Uranium") { // WARNING the name of the element is hard coded
00249                 G4double* RelativAbundanceVec= Element->GetRelativeAbundanceVector();
00250                 for(long unsigned int i=0; i < Element->GetNumberOfIsotopes(); ++i) {
00251                     const G4Isotope* Isotope = Element->GetIsotope(i);
00252                     if(Isotope->GetName() == "U235") { // WARNING the name of the isotope is hard coded
00253                         fRelativeAbundanceU235 = RelativAbundanceVec[i]/Isotope->GetA();}
00254                     if(Isotope->GetName() == "U238") { // WARNING the name of the isotope is hard coded
00255                         fRelativeAbundanceU238 = RelativAbundanceVec[i]/Isotope->GetA();}
00256                 }
00257             }
00258         }
00259       }
00260       fbool_First = false;
00261   }
00262 }
