00110 //------------------------------------------------------------------------------*------------------------------------------------------------------------------//
00111 // Overloading the virtual method G4VUserDetectorConstruction::Construct()
00112 //------------------------------------------------------------------------------*------------------------------------------------------------------------------//
00113 G4VPhysicalVolume* GeomSVBR_AKME::Construct()
00114 {
00115     //const double M_PI = 3.14159265;
00116 
00117     // Cleanup old geometry
00118     G4GeometryManager::GetInstance()->OpenGeometry();
00119     G4PhysicalVolumeStore::GetInstance()->Clean();
00120     G4LogicalVolumeStore::GetInstance()->Clean();
00121     G4SolidStore::GetInstance()->Clean();
00122 
00123     // Material definition
00124     BuildMaterials();
00125 
00126     // To check volume overlapping (=true)
00127     overlap = false;
00128     if(p->Verbose >= 1) {
00129         overlap = true;
00130     }
00131 
00132     //--------------------------------------------------------------------------------------------------------------------------
00133 
00134     if(p->Verbose >= 1)
00135     {
00136         printf("=============================== SVBR Geometry Construction ==================================\n");
00137     }
00138 
00140     // Activation Panel
00141     //--------------------------------------------------------------------------
00142     //G4bool isReactorVesselPlacement = false;
00143     // G4bool isBeamPlacement              = false;
00144     G4bool isFlowGuidesPlacement        = true;
00145     G4bool isHeatExchangerPlacement     = true;
00146     G4bool isHeatExchangerPinsPlacement = true;
00147     G4bool isPurificationUnitPlacement  = true; // Skip for now the placement for 4-symmetry
00148     G4bool isMHDPlacement               = true;
00149     G4bool isMHDUnitCoolantPlacement    = true; // Gaz Injections // Skip for now the placement for 4-symmetry
00150     G4bool isReactorInternsPlacement    = true;
00151     G4bool isTopReflectorPlacement      = true;
00152     G4bool isFuelAssemblyPlacement      = true;
00153     G4bool isFuelPinsPlacement          = true;
00154     G4bool isBlanketPlacement           = true;
00155     G4bool isBottomReflectorPlacement   = true;
00156 
00157     G4bool isIncBlkt = true; // true: use 10%vol KTcO4 in Pb-Bi
00159 
00161     // Target Position
00162     //--------------------------------------------------------------------------
00163     //  When offset is 0, the spallation target top surface is at the
00164     // middle of the core.
00165     // For 600 MeV, the offset is around 15 cm and 25cm for 1 GeV
00166     //--------------------------------------------------------------------------
00167     // const G4double zOffsetTarget = 0.146 * m; // 0.171 * m; // (600 MeV => 17.1 cm according to EAP80 original file)
00169 
00171     // World Volume
00172     G4Box* sall = new G4Box("sall", 3.2 * m , 3.2 * m , 5.5 * m );
00173     G4LogicalVolume* lall = new G4LogicalVolume( sall, imat14, "lall");
00174 
00175     fWorld1 =
00176     new G4PVPlacement( 0,                                     // no rotation
00177         G4ThreeVector(),                                // at (0,0,0)
00178         lall,                                     // its logical volume
00179         "Universe",                                  // its name
00180         0,                                       // its mother volume
00181         false,                                     // no boolean operation
00182         0,                                       // copy number
00183         overlap
00184     );
00185 
00187     // Reactor Vessel
00188     const G4double zOffsetReactor = -1.2*m;
00189     G4Tubs* svesRoof = new G4Tubs("svesRoof", 0., 3.18 * m , 0.006 * m , 0., twopi);
00190     G4LogicalVolume* lvesRoof = new G4LogicalVolume( svesRoof, imat1, "lvesRoof" ); // Air
00191 
00192     new G4PVPlacement( 0, G4ThreeVector(0.*m, 0.*m, (6.67/*-0.855*/) * m + zOffsetReactor), lvesRoof, "pvesRoof", lall, false, 0, overlap);
00193 
00194 
00195     G4Tubs* scovPlate = new G4Tubs("scovPlate", 0., 3.18 * m , 0.25 * m , 0., twopi);
00196     G4LogicalVolume* lcovPlate = new G4LogicalVolume( scovPlate, Cr9MatVector[0]/*imat2*/, "lcovPlate" ); // SS316L
00197     vectSS316L_LV.push_back(lcovPlate);
00198 
00199     new G4PVPlacement( 0, G4ThreeVector(0.*m, 0.*m, (6.414/*-0.855*/) * m + zOffsetReactor), lcovPlate, "pcovPlate", lall, false, 0, overlap);
00200 
00201     G4VSolid* ssafevesO = new G4Tubs("ssafevesO", 0.*3.14 * m , 3.18 * m , 3.61 * m , 0., twopi);
00202     G4Sphere* sSphsafevesO = new G4Sphere ("sSphsafevesO", 0.*3.14 * m , 3.18 * m , 0., twopi, 90. * deg, 90. * deg);
00203     G4VSolid* ssafevesO0 = new G4UnionSolid("ssafevesO0", sSphsafevesO, ssafevesO, 0, G4ThreeVector(0., 0., 3.61 * m ));
00204     G4LogicalVolume* lsafevesO = new G4LogicalVolume( ssafevesO0, Cr9MatVector[1]/*imat3*/, "lsafevesO" ); // SS316L
00205     lReactorOuterVessel = lsafevesO;
00206     vectSS316L_LV.push_back(lsafevesO);
00207 
00208     new G4PVPlacement( 0, G4ThreeVector(0.*m , 0.*m , (-1.056/*-0.855*/) * m + zOffsetReactor), lsafevesO, "psafevesO", lall, false, 0, overlap);
00209 
00210     // Gap between safety vessel and reactor vessel
00211     G4VSolid* ssafevesI = new G4Tubs("ssafevesI", 0.*3. * m , 3.14 * m , 3.61 * m , 0., twopi);
00212     G4Sphere* sSphsafevesI = new G4Sphere ("sSphsafevesI", 0.*3. * m , 3.14 * m , 0., twopi, 90. * deg, 90. * deg);
00213     G4VSolid* ssafevesI0 = new G4UnionSolid("ssafevesI0", sSphsafevesI, ssafevesI, 0, G4ThreeVector(0., 0., 3.61 * m ));
00214     G4LogicalVolume* lsafevesI = new G4LogicalVolume( ssafevesI0, inertGasMatVector[0]/*imat4*/, "lsafevesI" ); // Air
00215 
00216     G4VSolid* sreacvesO = new G4Tubs("sreacvesO", 0.*2.96 * m , 3. * m , 3.6 * m , 0., twopi);
00217     G4Sphere* sSphreacvesO = new G4Sphere ("sSphreacvesO", 0.*2.96 * m , 3. * m , 0., twopi, 90. * deg, 90. * deg);
00218     G4VSolid* sreacvesO0 = new G4UnionSolid("sreacvesO0", sSphreacvesO, sreacvesO, 0, G4ThreeVector(0., 0., 3.6 * m ));
00219     G4LogicalVolume* lreacvesO = new G4LogicalVolume( sreacvesO0, Cr9MatVector[2]/*imat5*/, "lreacvesO" ); // SS316L
00220     lReactorInnerVessel = lreacvesO;
00221     vectSS316L_LV.push_back(lreacvesO);
00222 
00223     // Inside of reactor vessel, contains primary coolant plus all internals
00224     const G4double halfHeight_reactorVesselIn = 3.6 * m;
00225     G4VSolid* sreacvesI = new G4Tubs("sreacvesI", 0. * m , 2.96 * m , halfHeight_reactorVesselIn, 0., twopi);
00226     G4Sphere* sSphreacvesI = new G4Sphere ("sSphreacvesI", 0. * m , 2.96 * m , 0., twopi, 90. * deg, 90. * deg);
00227     G4VSolid* sreacvesI0 = new G4UnionSolid("sreacvesI0", sSphreacvesI, sreacvesI, 0, G4ThreeVector(0., 0., halfHeight_reactorVesselIn));
00228     G4LogicalVolume* lreacvesI = new G4LogicalVolume( sreacvesI0, pbBiMatVector[0]/*imat6*/, "lreacvesI" ); // Pb-Bi
00229     vectPrimaryCoolant_LV.push_back(lreacvesI);
00230 
00231     new G4PVPlacement( 0, G4ThreeVector(), lreacvesI, "pHoldint", /*lall*/lreacvesO, false, 0, overlap);
00232 
00233     new G4PVPlacement( 0, G4ThreeVector(), lreacvesO, "preacvesO", /*lall*/lsafevesI, false, 0, overlap);
00234 
00235     new G4PVPlacement( 0, G4ThreeVector(), lsafevesI, "psafevesI", /*lall*/lsafevesO, false, 0, overlap);
00236 
00238     // Heat exchangers
00239     if (isHeatExchangerPlacement) {
00240         const G4double halfWidth_heatExOuterShell = 0.3775 * m;
00241         const G4double halfLength_heatExOuterShell = 0.8 * m;
00242         const G4double halfWidth_heatExInnerShell = halfWidth_heatExOuterShell - 1. * cm;
00243         const G4double halfLength_heatExInnerShell = halfLength_heatExOuterShell - 1. * cm;
00244         const G4double halfHeight_heatExShell   = 3.15 * m;
00245 
00246         const G4double outerRadius_heatExPin = 0.011 * m;
00247         const G4double halfHeight_heatExPin = 0.3 * m;
00248         //const G4double lengthLattice = 0.03 * m; // Pitch between pins (distance between center of pins)
00249         const G4double width_cell = 2. * 0.015 * m; // 0.030625 * m;
00250         const G4double length_cell = 2. * 0.015 * m; // 0.0316 * m;
00251 
00252         const double nbHeatExchanger = 4.;
00253         const double angleOffset = twopi/8.;
00254         const G4double pitch_heatExchanger = 2.4 * m; // Distance between center HX and center reactor
00255 
00257         // Heat Echanger Core
00258         G4Box* shxwall1 = new G4Box("shxwall1", halfWidth_heatExOuterShell, halfLength_heatExOuterShell, halfHeight_heatExShell);
00259         G4LogicalVolume* lhxwall1 = new G4LogicalVolume( shxwall1, Cr9MatVector[8]/*imat21*/, "lhxwall1"); // SS316L
00260         vectSS316L_LV.push_back(lhxwall1);
00261 
00262         G4Box* shxin1 = new G4Box("shxin1", halfWidth_heatExInnerShell, halfLength_heatExInnerShell, halfHeight_heatExShell);
00263         G4LogicalVolume* lhxin1 = new G4LogicalVolume( shxin1, pbBiMatVector[3]/*imat22*/, "lhxin1"); // Pb-Bi
00264 
00265         new G4PVPlacement( 0, G4ThreeVector(), lhxin1, "phxin1", lhxwall1, false, 0, overlap);
00267 
00268         if (isHeatExchangerPinsPlacement) {
00270             // Lattice Placement
00271 
00272             // Placement
00273             const G4double zOffsetHXPins = 0.75 * m;
00274             // G4VPhysicalVolume* pHXPinsDivisionZ = new G4PVDivision("pHXPinsReplicaXDivisionZ", lHXPinLevel, lhxin1, kZAxis, 8, 2.*halfHeight_heatExPin, zOffsetHXPins);
00275             G4double zPos = -halfHeight_heatExShell + zOffsetHXPins + halfHeight_heatExPin;
00276             std::string indZ;
00277             for (int i=0; i<8; i++) {
00278                 indZ = std::to_string(i);
00281                 G4Box* sHXPinLevel = new G4Box("sHXPinLevel_"+indZ, halfWidth_heatExInnerShell, halfLength_heatExInnerShell, halfHeight_heatExPin);
00282                 G4LogicalVolume* lHXPinLevel = new G4LogicalVolume(sHXPinLevel, pbBiMatVector[3], "lHXPinLevel_"+indZ); // Pb-Bi
00283                 new G4PVPlacement( 0, G4ThreeVector(0., 0., zPos), lHXPinLevel, "pHXPinsReplicaXDivisionZ_"+indZ, lhxin1, false, i, overlap);
00284                 zPos += 2. * halfHeight_heatExPin;
00285 
00287                 // Row
00288                 G4Box* sHXPinRow = new G4Box("sHXPinRow_"+indZ, halfWidth_heatExInnerShell, length_cell/2., halfHeight_heatExPin);
00289                 G4LogicalVolume* lHXPinRow = new G4LogicalVolume(sHXPinRow, pbBiMatVector[3], "lHXPinRow_"+indZ); // Pb-Bi
00290                 // G4VPhysicalVolume* pHXPinsReplicaX = new G4PVReplica("pHXPinsReplicaX", lHXPinRow, pHXPinsDivisionZ, kYAxis, 50, length_cell);
00291                 int nRow = 51;
00292                 G4double yPos = length_cell * (-(double)nRow/2. + 0.5);
00293                 for (int j=0; j<nRow; j++) {
00294                     new G4PVPlacement( 0, G4ThreeVector(0., yPos, 0.), lHXPinRow, "pHXPinsRowY_"+indZ+std::to_string(j), lHXPinLevel, false, j, overlap);
00295                     yPos += length_cell;
00296                 }
00297 
00299                 // Cell
00300                 G4Box* sHXPinBox = new G4Box("sHXPinBox_"+indZ, width_cell/2., length_cell/2., halfHeight_heatExPin);
00301                 G4LogicalVolume* lHXPinBox = new G4LogicalVolume(sHXPinBox, pbBiMatVector[3], "lHXPinBox_"+indZ); // Pb-Bi
00302                 // new G4PVReplica("pHXPinsReplicaY", lHXPinBox, pHXPinsReplicaX, kXAxis, 24, width_cell);
00303                 int nCell = 23;
00304                 G4double xPos = width_cell * (-(double)nCell/2. + 0.5);
00305                 for (int j=0; j<nCell; j++) {
00306                     new G4PVPlacement( 0, G4ThreeVector(xPos, 0., 0.), lHXPinBox, "pHXPinsCellX_"+indZ+std::to_string(j), lHXPinRow, false, j, overlap);
00307                     xPos += width_cell;
00308                 }
00309 
00311                 // Init Heat Exchanger Pin
00312                 G4Tubs* stubeO10 = new G4Tubs("stubeO_"+indZ, 0., outerRadius_heatExPin, halfHeight_heatExPin, 0., twopi);
00313                 G4LogicalVolume* ltubeO10 = new G4LogicalVolume( stubeO10, Cr9MatVector[9]/*imat23*/, "ltubeO_"+indZ); // SS316L
00314                 vectSS316L_LV.push_back(ltubeO10);
00315 
00316                 G4Tubs* stubeI10 = new G4Tubs("stubeI_"+indZ, 0.*0.0079 * m, 0.0098 * m, halfHeight_heatExPin, 0., twopi);
00317                 G4LogicalVolume* ltubeI10 = new G4LogicalVolume( stubeI10, pbBiMatVector[3]/*imat22*/, "ltubeI_"+indZ); // Pb-Bi
00318                 //vectPrimaryCoolant_LV.push_back(ltubeI10);
00319 
00320                 G4Tubs* scoolO10 = new G4Tubs("scoolO_"+indZ, 0., 0.0079*m, halfHeight_heatExPin, 0., twopi);
00321                 G4LogicalVolume* lcoolO10 = new G4LogicalVolume( scoolO10, Cr9MatVector[9]/*imat23*/, "lcoolO1_"+indZ); // SS316L
00322                 vectSS316L_LV.push_back(lcoolO10);
00323 
00324                 G4Tubs* sSecondaryCoolant = new G4Tubs("sSecondaryCoolant_"+indZ, 0., 0.0070 * m, halfHeight_heatExPin, 0., twopi);
00325                 G4LogicalVolume* lSecondaryCoolant = new G4LogicalVolume( sSecondaryCoolant, orgCoolMatVector[i]/*imat24*/, "lSecondaryCoolant_"+indZ); // Organic Coolant
00326                 vectSecondaryCoolant_LV.push_back(lSecondaryCoolant);
00327 
00328                 // Build
00329                 new G4PVPlacement( 0, G4ThreeVector(), lSecondaryCoolant, "pSecondaryCoolant_"+indZ, lcoolO10, false, 0, overlap);
00330                 new G4PVPlacement( 0, G4ThreeVector(), lcoolO10, "pcoolO_"+indZ, ltubeI10, false, 0, overlap);
00331                 new G4PVPlacement( 0, G4ThreeVector(), ltubeI10, "ptubeI_"+indZ, ltubeO10, false, 0, overlap);
00332 
00333                 // Placement
00334                 new G4PVPlacement( 0, G4ThreeVector(), ltubeO10, "ptubeO_"+indZ, lHXPinBox/*lhxin1*/, false, 0, overlap);
00335                 // End Heat Exchanger Pin
00337             }
00338         }
00339 
00340         std::string indHX, ext;
00341         for (int l=0; l < (int)nbHeatExchanger; l++) { // Loop number of Heat Exchanger
00342             indHX = std::to_string(l);
00343             ext = "_"+indHX;
00344 
00345             G4RotationMatrix* zRot = new G4RotationMatrix(); // Rotates Z axes only
00346             zRot->rotateZ(l*0.5*M_PI*rad - angleOffset);
00347 
00348             new G4PVPlacement( zRot,
00349                 G4ThreeVector(pitch_heatExchanger*cos((double)l*twopi/nbHeatExchanger + angleOffset),
00350                               pitch_heatExchanger*sin((double)l*twopi/nbHeatExchanger + angleOffset),
00351                               (3.014+1.036) * m),
00352                 lhxwall1,
00353                 "pHeatExchanger"+ext,
00354                 lreacvesI,
00355                 false,
00356                 l, // nb of copy
00357                 overlap
00358             );
00359         }
00360     }
00361 
00363     // Purification Units
00364     if (isPurificationUnitPlacement) { // Skip the placement for 4-symmetry
00365         const G4double r = 2.4 * m;
00366         const G4double angOffset = 0.*M_PI/4.;
00367         const double nbComponents = 4.;
00368         for (double i=1.; i<nbComponents-1.; i++) {
00369             std::string ind = std::to_string((int)i);
00370             // Init
00371             G4VSolid* spurifO1 = new G4Tubs("spurifO"+ind, 0.*0.342 * m , 0.35 * m , 2.5375 * m , 0., twopi);
00372             G4Sphere* sSphpurifO1 = new G4Sphere ("sSphpurifO"+ind, 0.*0.342 * m , 0.35 * m , 0., twopi, 90. * deg, 90. * deg);
00373             G4VSolid* spurifO10 = new G4UnionSolid("spurifO_un"+ind, sSphpurifO1, spurifO1, 0, G4ThreeVector(0., 0., 2.5375 * m )); // SS316L
00374             G4LogicalVolume* lpurifO1 = new G4LogicalVolume( spurifO10, Cr9MatVector[10]/*imat25*/, "lpurifO"+ind );
00375             vectSS316L_LV.push_back(lpurifO1);
00376 
00377             G4VSolid* spurifI1 = new G4Tubs("spurifI"+ind, 0. * m , 0.342 * m , 2.5375 * m , 0., twopi);
00378             G4Sphere* sSphpurifI1 = new G4Sphere ("sSphpurifI"+ind, 0. * m , 0.342 * m , 0., twopi, 90. * deg, 90. * deg);
00379             G4VSolid* spurifI10 = new G4UnionSolid("spurifI_un"+ind, sSphpurifI1, spurifI1, 0, G4ThreeVector(0., 0., 2.5375 * m ));
00380             G4LogicalVolume* lpurifI1 = new G4LogicalVolume( spurifI10, pbBiMatVector[4]/*imat26*/, "lpurifI"+ind ); // Pb-Bi
00381 
00382             // Build
00383             new G4PVPlacement( 0, G4ThreeVector(), lpurifI1, "ppurifI"+ind, lpurifO1, false, 0, overlap);
00384 
00385             // Place
00386             new G4PVPlacement( 0,
00387                 G4ThreeVector(r*cos(i*twopi/nbComponents + angOffset), r*sin(i*twopi/nbComponents + angOffset), 1.089 * m ) - /*G4ThreeVector(0. * m , 0. * m , 2.564 * m )*/G4ThreeVector(0. * m , 0. * m , -1.036 * m ),
00388                 lpurifO1,
00389                 "ppurifO"+ind,
00390                 lreacvesI,
00391                 false,
00392                 0,
00393                 overlap
00394             );
00395         }
00396     }
00397 
00398 
00400     // Core
00402     const int nCoreSector = 4;
00403     const G4double dPhi_core = twopi/((double)nCoreSector);
00404 
00405     // Core Position inside the reactor
00406     const G4double zPos_core = (-0.276+1.036) * m; // Position accroding to the center of lreacvesI at the junction the bottom of the cylinder and the sphere
00407 
00408     // Core
00409     const G4double halfHeight_topReflector = 0.323 * m;
00410     const G4double halfHeight_coreFuelAssembly = 0.633 * m;
00411     const G4double halfHeight_bottomReflector = 0.5525 * m;
00412 
00413     // Dimensions Core Interns
00414     const G4double halfHeight_coreTop = 0.035 * m;
00415     const G4double halfHeight_coreBot = 0.0805 * m;
00416     const G4double halfHeight_coreIntern = halfHeight_topReflector + halfHeight_coreFuelAssembly + halfHeight_bottomReflector;
00417     const G4double halfHeight_core = halfHeight_coreTop + halfHeight_coreIntern + halfHeight_coreBot; //1.624 * m;
00418 
00419     const G4double outerRadius_targwalO = 0.; // 0.283 * m; // Outer most radius of the Beam Pipe Envelope
00420 
00421     const G4double innerRadius_core = 0.; // 0.3149*m/*outerRadius_targwalO*/;
00422     const G4double outerRadius_coreFuelAssembly = 0.85129 * m;
00423     const G4double outerRadius_blanket = 1.27816 * m;
00424     const G4double outerRadius_core = 1.9 * m;
00425     const G4double thickness_coreNeutronShield = 0.025 * m;
00426     const G4double innerRadius_coreNeutronShield = outerRadius_core - thickness_coreNeutronShield;
00427 
