00429     const G4double zPos_coreTop =  halfHeight_core - halfHeight_coreTop; // 1.589
00430     const G4double zPos_coreBot = -halfHeight_core + halfHeight_coreBot; // -1.5435
00431     const G4double zPos_coreIntern = halfHeight_core - (2.*halfHeight_coreTop + halfHeight_coreIntern); // 0.0455
00432 
00434     // Fuel Assemblies Lattice in a ring
00435     const G4double halfLength_coreFuelAssemblyWall = 0.062164 * m;
00436     const G4double halfLength_coreFuelAssemblyCool = 0.060303 * m;
00437     const G4double thickness_coreFuelAssemblyWall = halfLength_coreFuelAssemblyWall - halfLength_coreFuelAssemblyCool;
00438     const double zOffsetAssembly = 0.001;
00439 
00441     // Assembly Parameters
00442     const G4double pitch_assemblies = 0.064025*2.* m; // Pitch between pins (distance between center of pins)
00443 
00445     // Blanket
00446     const G4double innerRadius_blanket = outerRadius_coreFuelAssembly;
00447     const G4double halfHeight_blanket = halfHeight_coreFuelAssembly;
00448     const G4double halfLength_blanketWall = halfLength_coreFuelAssemblyWall;
00449 
