00467     const G4double zPos_coreFuelAssembly = zPos_coreIntern + halfHeight_coreIntern - 2.*halfHeight_topReflector - halfHeight_coreFuelAssembly;//(-zOffsetAssembly+0.2305) * m + zPos_coreIntern;
