00475     // Core Structure contain the entire core with reflectors and shields
00476     G4Tubs* sCoreStructure = new G4Tubs("sCoreStructure", /*0.3149*m*/outerRadius_targwalO, outerRadius_core, halfHeight_core, 0., twopi); // Primary Coolant Core
00477     G4LogicalVolume* lCoreStructure = new G4LogicalVolume( sCoreStructure, pbBiMatVector[0]/*imat6*/, "lCoreStructure" ); // Pb-Bi
00478     vectPrimaryCoolant_LV.push_back(lCoreStructure);
00479 
00480     new G4PVPlacement( 0, G4ThreeVector(0., 0., zPos_core), lCoreStructure, "pCoreStructure", lreacvesI, false, 0, overlap);
00482 
00484     // Core Quarter
00485     // 4-symmetry of the core possible with square lattice
00487     // Init
00488     G4Tubs* sCoreQuarter = new G4Tubs("sCoreQuarter", /*0.3149*m*/outerRadius_targwalO, outerRadius_core, halfHeight_core, 0., dPhi_core); // Primary Coolant Core
00489     G4LogicalVolume* lCoreQuarter = new G4LogicalVolume( sCoreQuarter, pbBiMatVector[0]/*imat6*/, "lCoreQuarter" ); // Pb-Bi
00490     vectPrimaryCoolant_LV.push_back(lCoreQuarter);
00491 
