00496 
00498     const G4double pitch_coreQuarter = 0.*m;
00499     std::string indCore, ext;
00500     for (int i=0; i<nCoreSector; i++) {
00501         indCore = std::to_string(i);
00502         ext = "_"+indCore;
00503 
