00569         double bound = 60.;
00570         G4double xStep, yStep, r, halfDiag_topReflectorWall = halfLength_topReflectorWall*sqrt(2.);
00571         std::string indX, indY;
00572         int countTopReflectorPin = 0;
00573         for (double i=0.; i<bound; i++) {
00574             for (double j=0.; j<bound; j++) {
00575                 xStep = (i+0.5)*pitch_assemblies;
00576                 yStep = (j+0.5)*pitch_assemblies;
00577                 r = sqrt(xStep*xStep + yStep*yStep);
00578                 if (innerRadius_topReflector <= r-halfDiag_topReflectorWall && r+halfDiag_topReflectorWall <= outerRadius_topReflector) {
00579                     indX = std::to_string((int)i);
00580                     indY = std::to_string((int)j);
00581 
00582                     // Placement
00583                     new G4PVPlacement( 0, G4ThreeVector(xStep, yStep, zPos_topReflector), lrefwall1, "prefwall1_"+indX+"_"+indY, lCoreQuarter, false, countTopReflectorPin, overlap);
00584                     countTopReflectorPin++;
00585                 }
00586             }
00587         }
00588     }
