01731         G4LogicalVolume* lupflowO = new G4LogicalVolume( supflowO, imat17, "lupflowO" ); // 50% SS316L, 50% void
01732         vectSS316L_LV.push_back(lupflowO);
01733 
01734         G4Tubs* supflowI = new G4Tubs("supflowI", 0.3149*m/*outerRadius_targwalO*/, 0.805 * m , 2.14 * m , 0., twopi ); // Inside Upper
01735         G4LogicalVolume* lupflowI = new G4LogicalVolume( supflowI, pbBiMatVector[0]/*imat6*/, "lupflowI" ); // Pb-Bi
01736         vectPrimaryCoolant_LV.push_back(lupflowI);
01737 
01738         G4Tubs* supflowT = new G4Tubs("supflowT", 0.3149*m/*outerRadius_targwalO*/ , 0.83 * m , 0.05 * m , 0., twopi ); // Top
01739         G4LogicalVolume* lupflowT = new G4LogicalVolume( supflowT, imat18, "lupflowT" ); // 20% SS316L, 80% Pb-Bi
01740 
01741         G4Tubs* supflowB = new G4Tubs("supflowB", 0.3149*m/*outerRadius_targwalO*/ , 0.83 * m , 0.05 * m , 0., twopi ); // Bottom
01742         G4LogicalVolume* lupflowB = new G4LogicalVolume( supflowB, imat18, "lupflowB" ); // 20% SS316L, 80% Pb-Bi
01743 
01744         // Lower flow
01745         G4Tubs* sloflowO = new G4Tubs("sloflowO", 0.3149*m/*outerRadius_targwalO*/+0.*1.5 * m, 1.56 * m , 0.4325 * m , 0., twopi ); // Lower flow
01746         G4LogicalVolume* lloflowO = new G4LogicalVolume( sloflowO, imat19, "lloflowO" ); // 90% SS316L, 10% void
01747         vectSS316L_LV.push_back(lloflowO);
01748 
01749         G4Tubs* sloflowI = new G4Tubs("sloflowI", 0.3149*m/*outerRadius_targwalO*/ , 1.5 * m , 0.4325 * m , 0., twopi ); // Lower inside
01750         G4LogicalVolume* lloflowI = new G4LogicalVolume( sloflowI, pbBiMatVector[0]/*imat6*/, "lloflowI" ); // 40% SS316L, 60% Pb-Bi
01751         vectPrimaryCoolant_LV.push_back(lloflowI);
01752 
01753         G4Tubs* sloflowT = new G4Tubs("sloflowT", 0.3149*m/*outerRadius_targwalO*/ , 1.56 * m , 0.176 * m , 0., twopi ); // top
01754         G4LogicalVolume* lloflowT = new G4LogicalVolume( sloflowT, imat20, "lloflowT" ); // 40% SS316L, 60% Pb-Bi
01755 
01756         G4Tubs* sloflowB = new G4Tubs("sloflowB", 0., 1.56 * m , 0.195 * m , 0., twopi); // Bottom
01757         G4LogicalVolume* lloflowB = new G4LogicalVolume( sloflowB, imat20, "lloflowB" ); // Pb-Bi
01758 
01759         // Placement
01761         new G4PVPlacement( 0, G4ThreeVector(0., 0., ( 6.064+1.036) * m), lupflowT, "pupflowT", lreacvesI, false, 0, overlap);
01762         new G4PVPlacement( 0, G4ThreeVector(), lupflowI, "pupflowI", lupflowO, false, 0, overlap);
01763         new G4PVPlacement( 0, G4ThreeVector(0., 0., ( 3.864+1.036) * m), lupflowO, "pupflowO", lreacvesI, false, 0, overlap);
01764         new G4PVPlacement( 0, G4ThreeVector(0., 0., ( 1.664+1.036) * m), lupflowB, "pupflowB", lreacvesI, false, 0, overlap);
01765 
01767         new G4PVPlacement( 0, G4ThreeVector(0., 0., (-2.076+1.036) * m), lloflowT, "ploflowT", lreacvesI, false, 0, overlap);
01768         new G4PVPlacement( 0, G4ThreeVector(), lloflowI, "ploflowI", lloflowO, false, 0, overlap);
01769         new G4PVPlacement( 0, G4ThreeVector(0., 0., (-2.6845+1.036) * m), lloflowO, "ploflowO", lreacvesI, false, 0, overlap);
01770         new G4PVPlacement( 0, G4ThreeVector(0., 0., (-3.312+1.036) * m), lloflowB, "ploflowB", lreacvesI, false, 0, overlap);
01771     }
01772 
01774 
01775 
01776 
01777     VisualisationConfig();
01778 
01780     // Names
01781     for (size_t i=0; i<vectPrimaryCoolantCore_LV.size(); i++) {
01782         m_vectPrimaryCoolantCoreName.push_back(vectPrimaryCoolantCore_LV[i]->GetName());
01783     }
01784     for (size_t i=0; i<vectPrimaryCoolant_LV.size(); i++) {
01785         m_vectPrimaryCoolantName.push_back(vectPrimaryCoolant_LV[i]->GetName());
01786     }
01787     for (size_t i=0; i<vectSecondaryCoolant_LV.size(); i++) {
01788         m_vectSecondaryCoolantName.push_back(vectSecondaryCoolant_LV[i]->GetName());
01789     }
01790     for (size_t i=0; i<vectFuel_LV.size(); i++) {
01791         m_vectFuelName.push_back(vectFuel_LV[i]->GetName());
01792     }
01793     // m_spallationTargetName = lSpallationTarget->GetName();
01794     m_reactorOuterVesselName = lReactorOuterVessel->GetName();
01795     m_reactorInnerVesselName = lReactorInnerVessel->GetName();
01796 
01797 
01799 
01800     if (p->Verbose >= 1) {
01801         std::cout << "*** Print masses ***" << std::endl;
01802         for (size_t i = 0; i < fuelMatVector.size(); i++) {
01803             if (fuelMatVector[i]) {
01804                 fuelMatVector[i]->PrintMaterialVolumesMasses();
01805                 //fuelMatVector[i]->PrintMassLatticeByElement("U_235");
01806                 break;
01807             }
01808         }
01809         std::cout << "********************" << std::endl;
01810     }
01811 
01812     if(p->Verbose >= 2) {
01813         G4cout << *(G4Material::GetMaterialTable()) << G4endl;
01814     }
01815 
01816     return fWorld1;
01817 }
01818 
01819 void GeomSVBR_AKME::registerCopyNumbers(G4LogicalVolume* lv, G4int numberOfCopies)
01820 {
01821     m_NumberOfCopies[lv] = numberOfCopies;
01822     for(size_t i = 0; i < lv->GetNoDaughters(); i++) {
01823         m_NumberOfCopies[lv->GetDaughter(i)->GetLogicalVolume()] = numberOfCopies;
01824     }
01825 }
01826 
01827 G4int GeomSVBR_AKME::getNumberOfCopies(G4LogicalVolume* lvol)
01828 {
01829     std::unordered_map<G4LogicalVolume*, G4int>::iterator it = m_NumberOfCopies.find(lvol);
01830     if(it != m_NumberOfCopies.end())
01831     return m_NumberOfCopies[lvol];
01832     return 1;
01833 }
