03075 
03076 void GeomSVBR_AKME::VisualisationConfig() {
03077     if(p->Verbose >= 1) { std::cout << "\nConfiguration of the visualisation" << std::endl;}
03078 
03080     // visualisation colors configured by DL
03082     G4Colour black   ( .0   , .0    , .0         ); // black
03083     G4Colour lblack  ( .0   , .0    , .0    , 0.6); // light black
03084     G4Colour vlblack ( .0   , .0    , .0    , 0.2); // very light black
03085     G4Colour white   (1.0   , 1.0    , 1.0  , 0.3); // white
03086     G4Colour grayD3l (211   , 211    , 211       ); // gray D3 (light)
03087     G4Colour grayC   (0.5   , 0.5    , 0.5       ); // gray
03088     G4Colour flgrayC (0.5   , 0.5    , 0.5  , 0.8); // few light gray
03089     G4Colour lgrayC  (0.5   , 0.5    , 0.5  , 0.4); // light gray
03090     G4Colour vlgrayC (0.5   , 0.5    , 0.5  , 0.2); // very ligth gray
03091     G4Colour red     (1.0   , 0.0    , 0.0       ); // red
03092     G4Colour violet  (1.0   , 0.0    , 1.0       ); // violet
03093     G4Colour orange  (1.0   , 0.65   , 0.0       ); // orange
03094     G4Colour green   (0.0   , 1.0    , 0.0       ); // green
03095     G4Colour lgreen  (0.0   , 1.0    , 0.0  , 0.4); // light green
03096     G4Colour blue    (0.0   , 0.0    , 1.0       ); // blue
03097     G4Colour yellow  (1.0   , 1.0    , 0.0       ); // yellow
03098     G4Colour lyellow (1.0   , 1.0    , 0.0  , 0.2); // light yellow
03099     G4Colour cyan    (0.0   , 1.0    , 1.0  , 0.2); // cya
03100     G4Colour sky     (0.0   , 191.0/255.0, 1.0, 0.2); // sky
03101     G4Colour dgreen  (0.0   , 153.0/255.0, 153.0/255.0, 0.2); // dark green
03102     G4Colour cyangr  (0.0   , 204.0/255.0, 204.0/255.0   ); // cyan+green colour for different colour of h20
03103     G4Colour lblue   (0.0   , 0.0    , 1.0  , 0.4); // light blue
03104     G4Colour grayblu (0.4   , 0.6    , 0.8  , 0.6); // light gray blue
03105     G4Colour wcolour (0.3   , 0.3    , 0.3       );
03106     G4Colour Ccolour (0.    , 0.3    , 0.7       );
03107 
03109     G4bool bool_visible = true;
03110 
03111     G4VisAttributes* visWorld = new G4VisAttributes(white);
03112     visWorld->SetVisibility(false);
03113     if(fLogicWorld1) {fLogicWorld1->SetVisAttributes(visWorld);}
03114 
03115     // Stainless Steel Components
03116     G4VisAttributes* visSS316L = new G4VisAttributes(orange);
03117     visSS316L->SetVisibility(bool_visible);
03118     for (size_t i=0; i<vectSS316L_LV.size(); i++) {
03119         if(vectSS316L_LV[i]) {vectSS316L_LV[i]->SetVisAttributes(visSS316L);}
03120     }
03121 
03122     // Vessel
03123     G4VisAttributes* visVessel = new G4VisAttributes(orange);
03124     visVessel->SetVisibility(bool_visible);
03125     if(lReactorOuterVessel) {lReactorOuterVessel->SetVisAttributes(visVessel);}
03126     if(lReactorInnerVessel) {lReactorInnerVessel->SetVisAttributes(visVessel);}
03127 
03128     // Beam Vessel
03129     /*G4VisAttributes* visBeamVessel = new G4VisAttributes(blue);
03130     visBeamVessel->SetVisibility(bool_visible);
03131     if(lBeamVessel) {lBeamVessel->SetVisAttributes(visBeamVessel);}
03132 
03133     // Beam Pipe
03134     G4VisAttributes* visBeamPipe = new G4VisAttributes(orange);
03135     visBeamPipe->SetVisibility(bool_visible);
03136     if(lBeamPipe) {lBeamPipe->SetVisAttributes(visBeamPipe);}
03137 
03138     // Target
03139     G4VisAttributes* visTarget = new G4VisAttributes(red);
03140     visTarget->SetVisibility(bool_visible);
03141     if(lSpallationTarget) {lSpallationTarget->SetVisAttributes(visTarget);}*/
03142 
03143     // Core
03144     G4VisAttributes* visCore = new G4VisAttributes(violet);
03145     visCore->SetVisibility(bool_visible);
03146     if(lCore) {lCore->SetVisAttributes(visCore);}
03147 
03148     // Cladding
03149     G4VisAttributes* visClad = new G4VisAttributes(green);
03150     visClad->SetVisibility(bool_visible);
03151     for (size_t i=0; i<vectClad_LV.size(); i++) {
03152         if(vectClad_LV[i]) {vectClad_LV[i]->SetVisAttributes(visClad);}
03153     }
03154 
03155     // Fuel mat
03156     G4VisAttributes* visFuelMat = new G4VisAttributes(violet);
03157     visFuelMat->SetVisibility(bool_visible);
03158     for (size_t i=0; i<vectFuel_LV.size(); i++) {
03159         if(vectFuel_LV[i]) {vectFuel_LV[i]->SetVisAttributes(visFuelMat);}
03160     }
03161 
03162     // Fuel Assembly Color
03163     G4VisAttributes* visFuelAssemblyMat = new G4VisAttributes(violet);
03164     visFuelAssemblyMat->SetVisibility(bool_visible);
03165     for (size_t i=0; i<vectFuelAssembly_LV.size(); i++) {
03166         if(vectFuelAssembly_LV[i]) {vectFuelAssembly_LV[i]->SetVisAttributes(visFuelAssemblyMat);}
03167     }
03168 
03169     // Reflector Assembly Color
03170     G4VisAttributes* visReflector = new G4VisAttributes(green);
03171     visReflector->SetVisibility(bool_visible);
03172     for (size_t i=0; i<vectReflAssembly_LV.size(); i++) {
03173         if(vectReflAssembly_LV[i]) {vectReflAssembly_LV[i]->SetVisAttributes(visReflector);}
03174     }
03175 
03176     // Secondary Coolant Pin Color
03177     G4VisAttributes* visSecondaryCoolant = new G4VisAttributes(dgreen);
03178     visSecondaryCoolant->SetVisibility(bool_visible);
03179     for (size_t i=0; i<vectSecondaryCoolant_LV.size(); i++) {
03180         if(vectSecondaryCoolant_LV[i]) {vectSecondaryCoolant_LV[i]->SetVisAttributes(visSecondaryCoolant);}
03181     }
03182 
03183 
03184     if(p->Verbose >= 1) { std::cout << "End of VisualisationConfig" << std::endl;}
03185 }
