00134 void Run::RecordEvent(const G4Event* evt)
00135 {
00136   G4HCofThisEvent* HCE = evt->GetHCofThisEvent();
00137   if(!HCE) return;
00138   numberOfEvent++;
00139   for(int j=0;j<fNumberOfSD;j++)
00140   {
00141       const G4THitsMap<G4double>* evtMap = (G4THitsMap<G4double>*)(HCE->GetHC(colIDPara[j]));
00142       (*mapPara[j]) += *evtMap;
00143   }
00144 
00145   // G4Run::RecordEvent(evt);
00146 }
00147 
00148 G4double Run::GetTotal(const G4THitsMap<G4double> &map) const
00149 {
00150   G4double tot = 0.;
00151   std::map<G4int,G4double*>::iterator itr = map.GetMap()->begin();
00152   for(; itr != map.GetMap()->end(); itr++)
00153   { tot += *(itr->second); }
00154   return tot;
00155 }
00156 
00157 G4double Run::FindMinimum(const G4THitsMap<G4double> &map) const
00158 {
00159   G4double val = DBL_MAX;
00160   std::map<G4int,G4double*>::iterator itr = map.GetMap()->begin();
00161   for(; itr != map.GetMap()->end(); itr++)
00162   { if(val>*(itr->second)) val = *(itr->second); }
00163   return val;
00164 }
