00167 void Run::Initialize(G4int RunNumber, std::string ScoringMeshType) {
00168     m_threadId = G4Threading::G4GetThreadId();
00169     fNumberOfSD = 0;
00170     fEtot_deposit = 0;
00171     if (ScoringMeshType == "cartesian" || ScoringMeshType == "hexagonal" || ScoringMeshType == "polar") {
00172         fNumberOfSD = 5;
00173     }
00174     SetRunNumber(RunNumber);
00175     mapPara.clear();
00176     colIDPara.clear();
00177     G4String primName[5] = {"eDep","fluence_n","fluence_g","eDep_p","eDep_g"};
00178 
00179     G4SDManager* SDMan = G4SDManager::GetSDMpointer();
00180 
00181     G4String fullName;
00182     for(int j=0;j<fNumberOfSD;j++)
00183     {
00184         fullName = "mesh_det/"+primName[j];
00185         mapPara.push_back(new G4THitsMap<G4double>());
00186         colIDPara.push_back(SDMan->GetCollectionID(fullName));
00187     }
00188 }
