00335 void Run::Merge(std::map<TMXMaterial*, std::map<G4int,G4int>>& destinationMap,
00336                 const std::map<TMXMaterial*, std::map<G4int,G4int>>& sourceMap) const
00337 {
00338     for ( const auto& matData : sourceMap )
00339     {
00340         TMXMaterial* mat       = matData.first;
00341         if ( destinationMap.find(mat) == destinationMap.end())
00342             destinationMap[mat] = matData.second;
00343         else
00344         {
00345             for ( const auto& partData : sourceMap.at(mat) )
00346                 destinationMap[mat][partData.first] += partData.second;
00347         }
00348     }
00349 }
00350 
00355 void Run::Merge(const G4Run* run)
00356 {
00357     const Run* localRun = static_cast<const Run*>(run);
00358 
00359 
00360     //map: processes count
00361     for ( const auto& procCounter : localRun->fProcCounter )
00362     {
00363       G4String procName = procCounter.first;
00364       G4int localCount = procCounter.second;
00365       if ( fProcCounter.find(procName) == fProcCounter.end())
00366           fProcCounter[procName] = localCount;
00367       else
00368           fProcCounter[procName] += localCount;
00369     }
00370 
00371     //map: created particles count
00372     Merge(fParticleDataMap, localRun->fParticleDataMap);
00373 
00374     // std::cout << "Run Merge : " << fEtot_deposit << '\n';
00375     fEtot_deposit += localRun->GetEtotDeposit();
00376     // std::cout << "After : " << fEtot_deposit << '\n';
00377 
00378     //--------------------------------------------------------------------------*
00379     // Print localRun informations
00380     // if (m_p->Verbose >= 2 && localRun->m_scoringFluenceMap) {
00381     //     localRun->m_scoringFluenceMap->printSummary();
00382     // }
00383 
00384     G4Run::Merge(run);
00385 }
