00391 void Run::CountProcesses(const G4VProcess* process)
00392 {
00393     G4String procName = process->GetProcessName();
00394     std::map<G4String,G4int>::iterator it = fProcCounter.find(procName);
00395     if ( it == fProcCounter.end())
00396         fProcCounter[procName] = 1;
00397     else
00398         fProcCounter[procName]++;
00399 }
