00407 void Run::ParticleCount(G4int partName, G4int count, TMXMaterial* mat)
00408 {
00409     std::map<TMXMaterial*, std::map<G4int, G4int>>::iterator mat_it = fParticleDataMap.find(mat);
00410     if ( mat_it == fParticleDataMap.end())
00411     {
00412         std::map<G4int, G4int> a { {partName, count} };
00413         fParticleDataMap[mat] = a;
00414     }
00415     else
00416     {
00417         std::map<G4int, int>::iterator partName_it = fParticleDataMap[mat].find(partName);
00418         if(partName_it == fParticleDataMap[mat].end())
00419             fParticleDataMap[mat][partName] = count;
00420         else
00421             fParticleDataMap[mat][partName] += count;
00422     }
00423 }
