00605 void Run::WriteResultsInOutputRootFile() {
00606     if (m_threadId >= 0) {
00607         G4cout << "Write results in " << fRunNumber << "_" << m_threadId << "_" << m_p->str_ROOTFileName
00608                << " (" << GetNumberOfEvent() << " primary particles simulated) ... " << G4endl;
00609 
00610         // Histograms
00611         for (size_t i = 0; i < m_vectorScoringSurfaceCurrentSpectrum.size(); i++) {
00612             m_vectorScoringSurfaceCurrentSpectrum[i]->Write();
00613         }
00614         for (size_t i = 0; i < m_vectorScoringSurfaceFluenceSpectrum.size(); i++) {
00615             m_vectorScoringSurfaceFluenceSpectrum[i]->Write();
00616         }
00617         for (size_t i = 0; i < m_vectorScoringVolumeFluenceSpectrum.size(); i++) {
00618             m_vectorScoringVolumeFluenceSpectrum[i]->Write();
00619         }
00620 
00621         // Classical TH1D
00622         Write(m_MappingVol_Edep);
00623         Write(m_MappingEdep);
00624         Write(m_NeutronGenerationsHisto);
00625         Write(m_dN_dlogE_N0);
00626         Write(m_dN_dlogE_N0count);
00627         Write(m_MappingEdep_square);
00628         Write(m_MappingEdep_N);
00629         Write(m_MappingRadial_FluxN);
00630 
00631         // Maps
00632         for (size_t i = 0; i < m_vectorScoringFluenceMap.size(); i++) {
00633             m_vectorScoringFluenceMap[i]->Write();
00634         }
00635         for (size_t i = 0; i < m_vectorScoringPowerDensityMap.size(); i++) {
00636             m_vectorScoringPowerDensityMap[i]->Write();
00637         }
00638 
00639         // Classical TH2D
00640         Write(m_MappingXY_Edeposit);
00641         Write(m_MappingXY_FluxN);
00642         Write(m_MappingXY_FluxG);
00643         Write(m_MappingXY_Edep_p);
00644         Write(m_MappingXY_Edep_g);
00645         Write(m_MappingXY_Ede_ppSrc);
00646         Write(m_MappingXZ_FluxN);
00647         Write(m_MappingXY_Edep_targ);
00648         Write(m_MappingYZ_pSrc);
00649         Write(m_LogEvsLogTime);
00650         Write(m_MappingXZ_Edep);
00651         Write(m_MappingXZ_FluxG);
00652         Write(m_d2N_dlogElogTime_n);
00653         Write(m_d2N_dlogElogTime_g);
00654         Write(m_d2N_dlogElogTime_FFe);
00655         Write(m_d2N_dlogElogTime_others);
00656         Write(m_d2N_dlogElogTime_fission);
00657         Write(m_d2N_dlogElogTime_tot);
00658         Write(m_d2N_dlogElogTime_FFn);
00659         Write(m_d2N_dlogElogTime_FFg);
00660         Write(m_d2N_dlogElogTime_FFothers);
00661         Write(m_d2N_dlogElogTime_FFantiNu);
00662         Write(m_MappingXY_N0count);
00663         Write(m_MappingXZ_N0count);
00664         Write(m_MappingXY_N0);
