00670 
00671         // Ntuples
00672         if (m_scoringNtupleEverythingN) m_scoringNtupleEverythingN->Write();
00673         Write(m_scoringNtupleTransmutationInfo);
00674         Write(m_scoringNtupleDepositedPower);
