01225 
01226 void GeomEAP80Simplified::registerCopyNumbers(G4LogicalVolume* lv, G4int numberOfCopies)
01227 {
01228     m_NumberOfCopies[lv] = numberOfCopies;
01229     for(size_t i = 0; i < lv->GetNoDaughters(); i++)
01230     {
01231         m_NumberOfCopies[lv->GetDaughter(i)->GetLogicalVolume()] = numberOfCopies;
01232     }
01233 }
01234 
01235 G4int GeomEAP80Simplified::getNumberOfCopies(G4LogicalVolume* lvol)
01236 {
01237     std::unordered_map<G4LogicalVolume*, G4int>::iterator it = m_NumberOfCopies.find(lvol);
01238     if(it != m_NumberOfCopies.end())
01239         return m_NumberOfCopies[lvol];
01240     return 1;
01241 }
