01253 
01254 void GeomEAP80Simplified::MakeNeutronSource() {  // Build the Rez target with the beam pipe
01255 
01256     // Target definition
01257     m_TMXNeutronSource = new TMXNeutronSource(p);
01258 
01259     for(int i=0; i<p->SourceNumber; ++i) {
01260         G4ThreeVector Pos (p->src_XYZposition [i][0],p->src_XYZposition [i][1],p->src_XYZposition [i][2]);
01261         G4ThreeVector beam(p->src_XYZdirection[i][0],p->src_XYZdirection[i][1],p->src_XYZdirection[i][2]);
01262         m_TMXNeutronSource->placeGeometry(overlap, &Pos, &beam, fLogicWorld1);
01263     }
01264 
01265 //     std::cout << "build target assembly ok" << std::endl;
01266 }
