01268 void GeomEAP80Simplified::DefineMaterials()
01269 {
01271     // Void
01272     TMXMaterial* _Void = new TMXMaterial("Void_lead", 0.0001*(g/cm3), 4, kStateSolid, 900.0);
01273     _Void->AddElement(822040, 0.014); // Pb204
01274     _Void->AddElement(822060, 0.241); // Pb206
01275     _Void->AddElement(822070, 0.221); // Pb207
01276     _Void->AddElement(822080, 0.524); // Pb208
01277 
01279     const G4double smea = 0.25; // Smear [0; 1]
01280 
01282     // Dioxyde Uranium - Enrichement 15.6% U235
01283     /*TMXMaterial* _DU = new TMXMaterial("DU", 10.407*0.7*(g/cm3), 3, kStateSolid, 900.0);
01284     _DU->AddElement(922350,  156, "U5");
01285     _DU->AddElement(922380,  844, "U8");
01286     _DU->AddElement( 80160, 2000, "O_16");*/
01287 
01289     // Dioxyde 75%Thorium-25%Plutonium
01290     TMXMaterial* _ThPuO2 = new TMXMaterial("(75%Th-25%Pu)O2_fuel", 10.407*smea*(g/cm3), 7, kStateSolid, 900.0);
01291     /*_ThPuO2->AddElement(902320,  75, "Th232");
01292     _ThPuO2->AddElement(942390,  25, "Pu239");
01293     _ThPuO2->AddElement( 80160, 200, "O_16");*/
01294     _ThPuO2->AddElement( 80160, 20000, "O_16");
01295     _ThPuO2->AddElement(902320,  7500, "Th232");
01297     _ThPuO2->AddElement(942380,    87, "Pu238");
01298     _ThPuO2->AddElement(942390,  1297, "Pu239");
01299     _ThPuO2->AddElement(942400,   595, "Pu240");
01300     _ThPuO2->AddElement(942410,   323, "Pu241");
01301     _ThPuO2->AddElement(942420,   198, "Pu242");
01302 
01304     // Dioxyde 75%Thorium-25%Plutonium + 11% MA
01305     const G4double fracTh = 0.75;
01306     const G4double fracMA = 0.11;
01307     const G4double scaleFactor = 2400.;
01308     const std::string nameThPuO2_MA = "("+std::to_string((int)(fracTh*100.))+"%Th-"+std::to_string((int)((1.-fracTh)*100.))+"%Pu)O2 + "+std::to_string((int)(fracMA*100.))+"%MA";
01309     //                                        "(75%Th-25%Pu)O2 + 11%MA"
01310     TMXMaterial* _ThPuO2_MA = new TMXMaterial(nameThPuO2_MA, 10.407*smea*(g/cm3), 7, kStateSolid, 900.0);
01311     /*//// Dioxyde Th-Pu - 89% Atoms
01312     _ThPuO2_MA->AddElement(902320,  534, "Th232");
