00077 void SVBR100_Core::buildActiveCore() {
00078     G4double halfHeight_activeCore = m_halfHeight_neutronShield+m_halfHeight_bottomHolderWall;
00079     G4Tubs* sActiveCore = new G4Tubs("sCore_activeCore", m_innerRadius_activeCore, m_outerRadius_activeCore, halfHeight_activeCore, 0., twopi);
00080     m_lActiveCore = new G4LogicalVolume(sActiveCore, m_primaryCoolantMaterial, "lCore_activeCore");
00081 }
