00083 void SVBR100_Core::buildFuelAssemblyLattice() {
00084     m_coreLattice = new TMXLattice();
00085     // m_coreLattice->setPitch(m_pitchAssemblies); // Optionnal, can be set with the Map file
00086     G4RotationMatrix* zRot = new G4RotationMatrix; // Rotates on Z axes only
00087     zRot->rotateZ(30.*deg);
00088     m_coreLattice->setElementRotation(zRot);
00089 
00090     if (m_isBurnupDivision) {
00091         m_coreLattice->setLatticeMap(m_p->PathToCoreLatticeFile);
00092 
00093         for (int roundIndex = 1; roundIndex < 5; roundIndex++) {
00094             m_fuelAssembly = new SVBR100_FuelAssembly("SVBR100_FuelAssembly_"+std::to_string(roundIndex), m_p);
00095             m_fuelAssembly->setPrimaryCoolantMaterial(m_primaryCoolantMaterial);
00096             m_fuelAssembly->setMetalicStructureMaterial(m_metalicStructureMaterial);
00097             m_fuelAssembly->setNbFuelPinSections(m_nbAxialFuelPinDivision);
00098             m_fuelAssembly->setIndex(std::to_string(roundIndex));
00099             m_fuelAssembly->createGeometry();
00100             m_coreLattice->addDaughterVolume("F"+std::to_string(roundIndex), m_fuelAssembly->getMasterVolume());
00101         }
00102     }
00103     else {
00104         m_coreLattice->setLatticeMap(m_p->PathToCoreLatticeFile);
00105 
00106         m_fuelAssembly = new SVBR100_FuelAssembly("SVBR100_FuelAssembly", m_p);
00107         m_fuelAssembly->setPrimaryCoolantMaterial(m_primaryCoolantMaterial);
00108         m_fuelAssembly->setMetalicStructureMaterial(m_metalicStructureMaterial);
00109         m_fuelAssembly->setNbFuelPinSections(m_nbAxialFuelPinDivision);
00110         m_fuelAssembly->setIndex("F");
00111         m_fuelAssembly->createGeometry();
00112         m_coreLattice->addDaughterVolume("F", m_fuelAssembly->getMasterVolume());
00113 
00114         m_fuelAssembly = new SVBR100_FuelAssembly("SVBR100_FuelAssembly_CR", m_p);
00115         m_fuelAssembly->setPrimaryCoolantMaterial(m_primaryCoolantMaterial);
00116         m_fuelAssembly->setMetalicStructureMaterial(m_metalicStructureMaterial);
00117         m_fuelAssembly->setNbFuelPinSections(m_nbAxialFuelPinDivision);
00118         m_fuelAssembly->setIndex("CR");
00119         m_fuelAssembly->createGeometry();
00120         m_coreLattice->addDaughterVolume("CR", m_fuelAssembly->getMasterVolume());
00121         m_coreLattice->addDaughterAction("CR", std::bind(&SVBR100_FuelAssembly::placeControlRod, m_fuelAssembly, _1)); // https://stackoverflow.com/questions/12662891/how-can-i-pass-a-member-function-where-a-free-function-is-expected
00122 
00123         m_fuelAssembly = new SVBR100_FuelAssembly("SVBR100_FuelAssembly_CC", m_p);
00124         m_fuelAssembly->setPrimaryCoolantMaterial(m_primaryCoolantMaterial);
00125         m_fuelAssembly->setMetalicStructureMaterial(m_metalicStructureMaterial);
00126         m_fuelAssembly->setNbFuelPinSections(m_nbAxialFuelPinDivision);
00127         m_fuelAssembly->setIndex("CC");
00128         m_fuelAssembly->createGeometry();
00129         m_coreLattice->addDaughterVolume("CC", m_fuelAssembly->getMasterVolume());
00130         m_coreLattice->addDaughterAction("CC", std::bind(&SVBR100_FuelAssembly::placeControlRod, m_fuelAssembly, _1));
00131 
00132         m_fuelAssembly = new SVBR100_FuelAssembly("SVBR100_FuelAssembly_AC", m_p);
00133         m_fuelAssembly->setPrimaryCoolantMaterial(m_primaryCoolantMaterial);
00134         m_fuelAssembly->setMetalicStructureMaterial(m_metalicStructureMaterial);
00135         m_fuelAssembly->setNbFuelPinSections(m_nbAxialFuelPinDivision);
00136         m_fuelAssembly->setIndex("AC");
00137         m_fuelAssembly->createGeometry();
00138         m_coreLattice->addDaughterVolume("AC", m_fuelAssembly->getMasterVolume());
00139         m_coreLattice->addDaughterAction("AC", std::bind(&SVBR100_FuelAssembly::placeControlRod, m_fuelAssembly, _1));
00140 
00141         m_fuelAssembly = new SVBR100_FuelAssembly("SVBR100_FuelAssembly_EP", m_p);
00142         m_fuelAssembly->setPrimaryCoolantMaterial(m_primaryCoolantMaterial);
00143         m_fuelAssembly->setMetalicStructureMaterial(m_metalicStructureMaterial);
00144         m_fuelAssembly->setNbFuelPinSections(m_nbAxialFuelPinDivision);
