00161 void SVBR100_Core::buildNeutronShield() {
00162     G4double innerRadius_neutronShield = m_outerRadius_activeCore;
00163     //G4double thickness_neutronShield = m_outerRadius_neutronShield - innerRadius_neutronShield;
00164     m_zPosition_neutronShield = m_zPosition_bottomHolderWall + m_halfHeight_bottomHolderWall + m_halfHeight_neutronShield;
00165 
00166     G4Tubs* sNeutronShield = new G4Tubs("sCore_neutronShield", innerRadius_neutronShield, m_outerRadius_neutronShield, m_halfHeight_neutronShield, 0., twopi);
00167     m_lNeutronShield = new G4LogicalVolume(sNeutronShield, m_metalicStructureMaterial, "lCore_neutronShield");
00168 }
00169 
00170 void SVBR100_Core::buildNeutronShieldForMultipleTarget(G4ThreeVector translation, G4LogicalVolume* motherVolume) {
00171     G4double innerRadius_neutronShield = m_outerRadius_activeCore;
00172     m_zPosition_neutronShield = m_zPosition_bottomHolderWall + m_halfHeight_bottomHolderWall + m_halfHeight_neutronShield;
00173 
00174     G4double x, y;
00175     std::string ext;
00176     G4double thickness_wall = 5. * cm;
00177 
00178     G4double innerRadius_cornerShield = 455./2. * mm;
00179     G4double outerRadius_cornerShield = innerRadius_cornerShield + thickness_wall;
00180     G4double centerRadius_cornerShield = (outerRadius_cornerShield + innerRadius_cornerShield) / 2.;
00181     G4double centerRadius_neutronShield = (m_outerRadius_neutronShield + innerRadius_neutronShield)/2.;
00182     G4double halfLength_wallShield = (m_distanceTargetCenter - innerRadius_neutronShield)/2.;
00183     G4double angle_aperture = atan(centerRadius_cornerShield/centerRadius_neutronShield);
00184 
00185     G4Tubs* sCornerShield = new G4Tubs("sCornerShield", innerRadius_cornerShield, outerRadius_cornerShield, m_halfHeight_neutronShield, 3.*twopi/4., twopi/2.);
00186     G4Box* sWallShield = new G4Box("sWallShield", halfLength_wallShield, thickness_wall/2., m_halfHeight_neutronShield);
00187     G4Tubs* sNeutronShield = new G4Tubs("sCore_neutronShield", innerRadius_neutronShield, m_outerRadius_neutronShield, m_halfHeight_neutronShield, 0., twopi/(2.*(double)m_nbTarget) - angle_aperture);
00188 
00189     G4RotationMatrix* zRotNeutronShieldRight = new G4RotationMatrix; // Rotates on Z axes only
00190     zRotNeutronShieldRight->rotateZ(-angle_aperture);
00191     G4RotationMatrix* zRotNeutronShieldLeft = new G4RotationMatrix; // Rotates on Z axes only
00192     zRotNeutronShieldLeft->rotateZ(twopi/(2.*(double)m_nbTarget));
00193 
00194 
00195     for (int i = 0; i < m_nbTarget; i++) {
00196         ext = std::to_string(i);
00197         G4RotationMatrix* zRot = new G4RotationMatrix; // Rotates on Z axes only
00198         zRot->rotateZ(-twopi/((double)m_nbTarget) * (double)i - m_offsetAngle);
00199         x = m_distanceTargetCenter * cos((double)i/(double)m_nbTarget * twopi + m_offsetAngle);
00200         y = m_distanceTargetCenter * sin((double)i/(double)m_nbTarget * twopi + m_offsetAngle);
00201 
00202         G4VSolid* sChannelShield = new G4UnionSolid("sChannelShield", sCornerShield, sWallShield, 0, G4ThreeVector(-halfLength_wallShield, -centerRadius_cornerShield, 0.));
00203         sChannelShield = new G4UnionSolid("sChannelShield", sChannelShield, sWallShield, 0, G4ThreeVector(-halfLength_wallShield, +centerRadius_cornerShield, 0.));
00204         sChannelShield = new G4UnionSolid("sChannelShield", sChannelShield, sNeutronShield, zRotNeutronShieldRight, G4ThreeVector(-m_distanceTargetCenter, 0., 0.));
00205         sChannelShield = new G4UnionSolid("sChannelShield", sChannelShield, sNeutronShield, zRotNeutronShieldLeft, G4ThreeVector(-m_distanceTargetCenter, 0., 0.));
00206 
00207         // m_lNeutronShield = new G4LogicalVolume(sChannelShield, m_metalicStructureMaterial, "lCore_neutronShield");
00208 
00209         G4LogicalVolume* lChannelShield = new G4LogicalVolume(sChannelShield, m_reflectorMaterial/*m_metalicStructureMaterial*/, "lChannelShield"+ext);
00210 
00211         new G4PVPlacement(zRot, G4ThreeVector(x, y, m_zPosition_neutronShield) + translation, lChannelShield, "pChannelShield"+ext, motherVolume, false, 0, m_overlap);
00212     }
00213     // m_lNeutronShield = new G4LogicalVolume(sNeutronShield, m_metalicStructureMaterial, "lCore_neutronShield");
00214 }
