00216 void SVBR100_Core::buildInternalStructures() {
00218     // Bottom Fuel Assembly Holder
00219     G4double halfThickness_bottomHolder = 20./2. * cm;
00220     G4double outerRadius_bottomHolder = m_outerRadius_activeCore;
00221     m_zPosition_bottomHolder = -(1550./2. + 30./2.+110.) * mm /*-1845./2. * mm*/ - halfThickness_bottomHolder;
00222 
00223     G4Tubs* sBottomHolder = new G4Tubs("sCore_bottomHolder", 0., outerRadius_bottomHolder, halfThickness_bottomHolder, 0., twopi);
00224     m_lBottomHolder = new G4LogicalVolume(sBottomHolder, m_metalicStructureMaterial, "lCore_bottomHolder");
00225 
00227     // Bottom Fuel Assembly Holder Wall
00228     G4double outerRadius_bottomHolderWall = outerRadius_bottomHolder + 78. * mm;
00229     G4double innerRadius_bottomHolderWall = m_outerRadius_activeCore;
00230     m_zPosition_bottomHolderWall = m_zPosition_bottomHolder - m_halfHeight_bottomHolderWall + 521. * mm + halfThickness_bottomHolder;
00231 
00232     G4Tubs* sBottomHolderWall = new G4Tubs("sCore_bottomHolderWall", innerRadius_bottomHolderWall, outerRadius_bottomHolderWall, m_halfHeight_bottomHolderWall, 0., twopi);
00233     m_lBottomHolderWall = new G4LogicalVolume(sBottomHolderWall, m_metalicStructureMaterial, "lCore_bottomHolderWall");
00234 }
