00236 void SVBR100_Core::createMaterial() {
00237     // c ----- Be, 1.85 g/cm3
00238     // m4      1001.70c 8.565E-05
00239     //       1002.70c 1.545E-07
00240     //       1003.70c 1.659E-04
00241     //       2003.70c 5.109E-06
00242     //       2004.70c 8.891E-04
00243     //       3006.70c 1.204E-05
00244     //       3007.70c 1.374E-07
00245     //       4009.70c 9.988E-01
00246     //       5010.70c 3.795E-11
00247     //       5011.70c 1.185E-09
00248     //       6012     3.209E-14
00249     //       6013     3.838E-19
00250     // mt4    be.10t              $ 294 K
00251     m_reflectorMaterial = new TMXMaterial(
00252         "reflector", 1.85 * (g/cm3), 12, kStateSolid, 294. * kelvin
00253     );
00254     m_reflectorMaterial->AddElement(10010, 8.565E-05); // H1
00255     m_reflectorMaterial->AddElement(10020, 1.545E-07); // H2
00256     m_reflectorMaterial->AddElement(10030, 1.659E-04); // H3
00257     m_reflectorMaterial->AddElement(20030, 5.109E-06); // He3
00258     m_reflectorMaterial->AddElement(20040, 8.891E-04); // He4
00259     m_reflectorMaterial->AddElement(30060, 1.204E-05); // Li6
00260     m_reflectorMaterial->AddElement(30070, 1.374E-07); // Li7
00261     m_reflectorMaterial->AddElement(40090, 9.988E-01); // Be9
00262     m_reflectorMaterial->AddElement(50100, 3.795E-11); // B10
00263     m_reflectorMaterial->AddElement(50110, 1.185E-09); // B11
00264     m_reflectorMaterial->AddElement(60120, 3.209E-14); // C12
00265     m_reflectorMaterial->AddElement(60130, 3.838E-19); // C13
00266 }
