00271 void SVBR100_Core::placeGeometry(G4ThreeVector translation, G4LogicalVolume* motherVolume) {
00272     m_translationVector = translation;
00273 
00274     /*if (m_lActiveCore) {
00275         new G4PVPlacement(0, m_translationVector, m_lActiveCore, "pCore_activeCore", motherVolume, false, 0, m_overlap);
00276     }*/
00277 
00279     // Fuel Assembly Lattice
00280     if (m_coreLattice /*&& m_lActiveCore*/) {
00281         m_coreLattice->setMotherVolume(motherVolume/*m_lActiveCore*/);
00282         m_coreLattice->setElementTranslation(m_translationVector);
00283         m_coreLattice->placeLattice(m_overlap);
00284     }
00285 
00287     // Neutron Shield
00288     //if (m_lNeutronShield) {
00289     if (m_isMultipleExternalTarget) {
00290         buildNeutronShieldForMultipleTarget(G4ThreeVector(0., 0., m_zPosition_neutronShield) + m_translationVector, motherVolume);
00291     }
00292     else {
00293         buildNeutronShield();
00294         new G4PVPlacement(0, G4ThreeVector(0., 0., m_zPosition_neutronShield) + m_translationVector, m_lNeutronShield, "pCore_neutronShield", motherVolume, false, 0, m_overlap);
00295     }
00296     //
00297 
00299     // Bottom Fuel Assembly Holder
00300     if (m_lBottomHolder) {
00301         new G4PVPlacement(0, G4ThreeVector(0., 0., m_zPosition_bottomHolder) + m_translationVector, m_lBottomHolder, "pCore_bottomHolder", motherVolume, false, 0, m_overlap);
00302     }
00303 
00305     // Bottom Fuel Assembly Holder Wall
00306     if (m_lBottomHolderWall) {
00307         new G4PVPlacement(0, G4ThreeVector(0., 0., m_zPosition_bottomHolderWall) + m_translationVector, m_lBottomHolderWall, "pCore_bottomHolderWall", motherVolume, false, 0, m_overlap);
00308     }
00309 }
