00098 
00099     bool isPlotDisplay = false;
00100     bool isPlotXSDisplay = true;
00101     std::string DetectionMedia = /*"H3"; *//*"B10";*/ "U235";
00102 
00103     //-------------------------------- End Main --------------------------------*
00104 
00105     //--------------------------------------------------------------------------*
00106     // Extract the flux from a TMX Simulation
00107     int int_nbins = 1000;
00108     double dbl_Emin = 1E-5;
00109     double dbl_Emax = 1E9;
00110     double Emin_log = TMath::Log10(dbl_Emin);
00111     double Emax_log = TMath::Log10(dbl_Emax);
00112     double Ebin_width = ( Emax_log - Emin_log) /(double) int_nbins;
00113     double Ebins[int_nbins + 1];
00114 
00115     Ebins[0] = dbl_Emin;
00116     for (int i = 1; i <= int_nbins; i++) {
00117         Ebins[i] = dbl_Emin + TMath::Power(10, Emin_log + i * Ebin_width);
00118     }
00119 
00120 
00121 
00122     std::vector<TH1D*> histoFluxE_log;
00123     for(size_t i=0; i < spectraName.size(); ++i){
00124 
00125         if (isPlotDisplay) {
00126             TCanvas* canvasFlux = new TCanvas(spectraName[i].c_str(), spectraName[i].c_str());
00127             canvasSetup(canvasFlux);
00128         }
00129 
00130         histoFluxE_log.push_back(new TH1D);
00131         histoFluxE_log[i]->SetNameTitle(spectraName[i].c_str(), "");
00132         histoFluxE_log[i]->SetBins(int_nbins, Ebins); // Logarithmic binning
00133         TH1D* histoLog = (TH1D*)f_ROOT->Get(spectraName[i].c_str());
00134         histoLog->Scale(scaling_factor);
00135 
00136         double errorFlux = 0.;
00137         double integral = histoLog->IntegralAndError(0, (histoLog->GetNbinsX() + 1), errorFlux, ""); // eV
00138         double relativeError = errorFlux/integral;
00139 
00140         for (int j=1; j < histoLog->GetNbinsX(); j++) {
00141             // d (Phi) / d (ln(E))
00142             int bin_histoFluxElog = (int) (int_nbins * (histoLog->GetXaxis()->GetBinCenter(j) - Emin_log) / (Emax_log - Emin_log));
00143             histoFluxE_log[i]->SetBinError(bin_histoFluxElog, TMath::Sqrt( TMath::Power(histoFluxE_log[i]->GetBinError(j),2) + TMath::Power(histoLog->GetBinError(j) / (std::log(TMath::Power(10, histoLog->GetXaxis()->GetBinLowEdge(j+1)))-std::log(TMath::Power(10, histoLog->GetXaxis()->GetBinLowEdge(j)))), 2)));
00144             histoFluxE_log[i]->SetBinContent(bin_histoFluxElog, histoFluxE_log[i]->GetBinContent(bin_histoFluxElog) + histoLog->GetBinContent(j) / (std::log(TMath::Power(10, histoLog->GetXaxis()->GetBinLowEdge(j+1)))-std::log(TMath::Power(10, histoLog->GetXaxis()->GetBinLowEdge(j)))));
00145         }
00146         histoFluxE_log[i]->SetLineColorAlpha(1, 1);
00147         histoFluxE_log[i]->SetLineWidth(2);
00148         histoFluxE_log[i]->GetXaxis()->SetTitle("Neutron Energy [eV]");
00149         histoFluxE_log[i]->GetYaxis()->SetTitle("d{Phi}/d(ln(E)) [cm^{-2}.s^{-1}]");
00150 
00151         std::cout << "flux in " << spectraName[i] << ": " << histoFluxE_log[i]->IntegralAndError(0, (histoFluxE_log[i]->GetNbinsX() + 1), errorFlux, "") << "cm^{-2}.s^{-1}" << std::endl;
00152 
00153         if (isPlotDisplay) {
00154             histoFluxE_log[i]->Draw("HIST e");
00155 
00156         }
00157     }
00158 
00159     //--------------------------------------------------------------------------*
00160     // Extract the cross sections data from a data file
00161 
00162     if (isPlotXSDisplay) {
00163         TCanvas* canvasXS = new TCanvas(("XS " + DetectionMedia).c_str(), ("XS " + DetectionMedia).c_str());
00164         canvasSetup(canvasXS);
00165     }
00166 
00167 
00168     TH1D* histoXS_log = new TH1D;
00169     histoXS_log->SetNameTitle(("XS " + DetectionMedia).c_str(), "");
00170     histoXS_log->SetBins(int_nbins, Ebins); // Logarithmic binning
00171 
00172     std::string fileName = "XSDetectionTable.csv";
00173     std::string line, energyStr, crossSectionHStr, crossSectionBStr, crossSectionUStr, delimiter1, delimiter2, delimiter3;
00174     double energy, crossSection;
00175     std::ifstream file(fileName);
00176     int lineCount = 0;
00177     while (std::getline(file, line)) { // Read by line
00178         std::stringstream input(line);
00179         if (lineCount > 2) {
00180             input >> energyStr >> delimiter1 >> crossSectionHStr >> delimiter2 >> crossSectionBStr >> delimiter3 >> crossSectionUStr;
00181             if(DetectionMedia == "H3"){crossSection = std::stod(crossSectionHStr);}
00182             else if(DetectionMedia == "B10"){crossSection = std::stod(crossSectionBStr);}
00183             else if(DetectionMedia == "U235"){crossSection = std::stod(crossSectionUStr);}
00184             energy = std::stod(energyStr);
00185             histoXS_log->Fill(energy,crossSection);
00186 
00187         }
00188         lineCount++;
00189     }
00190 
00191     for(size_t i=1; i < int_nbins+1; ++i){
00192         histoXS_log->SetBinError(i,0.);
00193 
00194     }
00195 
00196 
00197     histoXS_log->GetXaxis()->SetTitle("Neutron Energy [eV]");
00198     histoXS_log->GetYaxis()->SetTitle("Cross Section [barn]");
00199     if(DetectionMedia == "U235")histoXS_log->Scale(10E-24*2.562E18); // XS for 1 mg of U235
00200     else if(DetectionMedia == "B10")histoXS_log->Scale(10E-24*6.022E19); // XS for 1 mg of B10
00201     else if(DetectionMedia == "H3")histoXS_log->Scale(10E-24*2.005E20); // XS for 1 mg of H3
00202 
00203     if (isPlotXSDisplay) {
00204         histoXS_log->Draw("HIST e");
00205     }
00206 
00207 
00208 
00209     //--------------------------------------------------------------------------*
00210     // Convolution
00211 
00212     std::vector<TH1D*> convolution;
00213     std::string fileout = "counts.csv";
00214     std::ofstream fileo(fileout, ios::out);
00215     if (fileo){
00216     for(size_t i=0; i<spectraName.size();++i){
00217         if (isPlotDisplay) {
00218             TCanvas* canvasConvolution = new TCanvas(("Convolution"+ std::to_string(i)).c_str(),("Convolution"+ std::to_string(i)).c_str());
00219             canvasSetup(canvasConvolution);
00220         }
00221         convolution.push_back((TH1D*)histoXS_log->Clone("Convolution"));
00222         convolution[i]->Multiply(histoFluxE_log[i]);
00223         convolution[i]->GetXaxis()->SetTitle("Neutron Energy [eV]");
00224         convolution[i]->GetYaxis()->SetTitle((DetectionMedia +" reaction rate [s^{-1}]").c_str());
00225         if (isPlotDisplay) {
00226             convolution[i]->Draw("HIST e");
00227         }
00228         double errorReactionRate = 0.;
00229         double totalReactionRate = convolution[i]->IntegralAndError(0, convolution[i]->GetNbinsX() + 1, errorReactionRate, "");
00230         double errorFlux = 0.;
00231 
00232         std::cout << "Estimation of count rate for " << spectraName[i] << ": " << totalReactionRate << "  " << errorReactionRate << " s^1/mg of "<< DetectionMedia << std::endl;
00233         fileo << totalReactionRate << "   " << errorReactionRate << "   " << histoFluxE_log[i]->IntegralAndError(0, (histoFluxE_log[i]->GetNbinsX() + 1), errorFlux, "")<< "   "<< errorFlux<<  std::endl;
00234     }
00235     fileo.close();
00236     }
