00054 // Add transmuted elements due to irradiation in a specific logical volume
00055 void TMXBatemanDecay::AddElement(int index, std::vector <TMXBatemanDecay::TransmutedElement>& vectorElement) {
00056 
00057 //     std::cout << "index : " << index << " sizeVec : " << vectorElement.size() << std::endl;
00058 //     if(index == 0) { std::cout << "********* Begin of AddElement ********* " << vectorElement.size() << std::endl;}
00059     for (auto element : vectorElement) {// access by value, the type of element is TMXBatemanDecay::TransmutedElement
00060         if( fMapLVMotherDaughter_transm[index][element.LogicVolumeName].empty() ) {
00061             fMapLVMotherDaughter_transm[index][element.LogicVolumeName] =   { {element.IZAmother, {{element.IZAdaughter, 1}} } };
00062 //             if(index == 0) { std::cout << "1element.IZAmother : " << element.IZAmother << " element.IZAdaughter : " << element.IZAdaughter << std::endl;}
00063         }
00064         else if( fMapLVMotherDaughter_transm[index][element.LogicVolumeName][element.IZAmother].empty() ) {
00065             fMapLVMotherDaughter_transm[index][element.LogicVolumeName][element.IZAmother] = { { {element.IZAdaughter, 1} } };
00066 //             if(index == 0) { std::cout << "2element.IZAmother : " << element.IZAmother << " element.IZAdaughter : " << element.IZAdaughter << std::endl;}
00067         }
00068         else if( fMapLVMotherDaughter_transm[index][element.LogicVolumeName][element.IZAmother][element.IZAdaughter] == 0 ) {
00069             fMapLVMotherDaughter_transm[index][element.LogicVolumeName][element.IZAmother][element.IZAdaughter] = 1;
00070 //             if(index == 0) { std::cout << "3element.IZAmother : " << element.IZAmother << " element.IZAdaughter : " << element.IZAdaughter << std::endl;}
00071         }
00072         else {
00073             ++fMapLVMotherDaughter_transm[index][element.LogicVolumeName][element.IZAmother][element.IZAdaughter];
00074 //             if(index == 0) { std::cout << "4element.IZAmother : " << element.IZAmother << " element.IZAdaughter : " << element.IZAdaughter << std::endl;}
00075         }
00076 //        std::cout<<element.IZAmother << " -> " << element.IZAdaughter << " "<< element.LogicVolumeName<<std::endl;
00077         // Sum of every components in 1 map
00078         ++fMapLVElem_transm[element.LogicVolumeName][element.IZAdaughter];
00079         --fMapLVElem_transm[element.LogicVolumeName][element.IZAmother];
00080     }
00081 //     if(index == 0) { std::cout << "********* End of AddElement *********" << std::endl;}
00082 }
