00094 // Print the number of transmuted elements due to irradiation
00095 void TMXBatemanDecay::DumpTransmutedElements() {
00096     for(int index=0; index<2; ++index){
00097         std::cout << index << "\n";
00098         for (std::unordered_map<std::string, std::unordered_map<int, std::unordered_map<int,long double>>>::iterator it=fMapLVMotherDaughter_transm[index].begin(); it!=fMapLVMotherDaughter_transm[index].end(); ++it) {
00099             std::cout << "\t" << it->first << "\n";
00100             for (std::unordered_map<int, std::unordered_map<int,long double>>::iterator it1=fMapLVMotherDaughter_transm[index][it->first].begin(); it1!=fMapLVMotherDaughter_transm[index][it->first].end(); ++it1) {
00101                 std::cout << "\t\t" << it1->first << "\n";
00102                 for (std::unordered_map<int,long double>::iterator it2=fMapLVMotherDaughter_transm[index][it->first][it1->first].begin(); it2!=fMapLVMotherDaughter_transm[index][it->first][it1->first].end(); ++it2) {
00103                     std::cout << "\t\t\t" << it2->first << "\t" << it2->second << std::endl;
00104                 }
00105             }
00106         }
00107     }
00108 }
