00121 std::unordered_map<int, long double> TMXBatemanDecay::GetMapNt() {
00122     auto it=fMapNt.begin();
00123     while ( it!=fMapNt.end()) {
00124         if(it->second < 1e-200 || it->first == 000000) { //|| std::log10(std::abs(fMapNt[IZA_i])) <= -100) {  // If amount is very low or if it is the ad hoc element used for fission
00125             it = fMapNt.erase(it); // WARNING not sure of 1e-200
00126         }
00127         else
00128             ++it;
00129     }
00130 
00131     return fMapNt;
00132 }
