00194 // Construct linear decay paths of element IZA due to decay or transmutation and update the map of decay paths
00195 // WARNING : Fissions or reaction with multiple products are not taken into account yet
00196 void TMXBatemanDecay::ConstructChain(int IZA, std::string LogicVolumeName="") {
00197     if (fMapChains.find(IZA) == fMapChains.end()) { // if element is not already in the map
00198 //         std::cout << "\n *** IZA found : " << IZA << std::endl;
00199 //     if (fMapChains[IZA].size() == 0) { // if element is not already in the map
00200         std::map<int, TMXElement*> mapStoreElem = TMXElementStore::Store();
00201         if( mapStoreElem[IZA]) {
00202             TMXElement* elem = mapStoreElem[IZA];
00203             long double lambda_star = lambdaStar(elem, LogicVolumeName);
00204 
00205             std::vector < std::vector < std::vector <long double> > > vectorChain;
00206             if(lambda_star != 0) { // if element is unstable or transmuted : implementation of the decay channel
00207                 std::vector < std::vector <long double> > vectorElement;
00208                 vectorChain.push_back(vectorElement);
00209 
00210                 RecursiveChain(elem, vectorChain, LogicVolumeName, lambda_star);  // Construct the chain recursively
00211             }
00212             else { vectorChain.push_back({{(double)IZA, 0, 0, 1, 0}}); }
00213             fMapChains[IZA] = vectorChain;      // Store the vector of chains in a map[key = IZA]
00214         }
00215         else
00216             std::cout << "Element " << IZA << " not found in the database." << std::endl;
00217     }
00218 }
00219 
00220 // Recursive construction of linear chains following each decay path successively
00221 void TMXBatemanDecay::RecursiveChain(TMXElement* MotherElem, std::vector < std::vector < std::vector <long double> > >& vectorChain, std::string LogicVolumeName, long double lambda_star) {
00222 
00223     std::vector < std::vector <long double> > vectorElement = vectorChain[vectorChain.size()-1]; // Read the last chain of elements
00224     std::map<int, TMXElement*> mapStoreElem = TMXElementStore::Store(); // Find the new element in the DB
00225     vector<TMXDecayChannel*> vectorDecays = MotherElem->DecayChannels(); // Decay channels of the new element
00226 
00227     // Loop over decay channels of the new element
00228     for( int i=0; i<(int)vectorDecays.size(); ++i ) {
00229         AddMotherAndProgeny(vectorChain, vectorElement, MotherElem, lambda_star, vectorDecays[i]->BranchingRatio()*lambda(MotherElem), 0., vectorDecays[i]->IZA(), i, LogicVolumeName, vectorDecays[i]->Decay(), 0);
00230     }
00231 
00232     // WARNING possibility of an Infinite loop
00233     // Loop over transmutation channels of the mother element to check if there is a transmutation of this element
00234     int IZA_mother = MotherElem->IZA();
00235     int i = 0;
00236     std::string strDecay = "";
00237     /*const std::size_t*/long double FissionRatePerN0 = 0;
00238     for(int index=0; index<2; ++index){
00239         if(index == 1) { // In the map reserved for fissions
00240             strDecay = "Fission"; // It is a fission
00241             // Calculation of the fission rate per N0 for the mother element = the sum of fission rates of every daughter elements divided by 2 because of the 2 fission products
00242             FissionRatePerN0 = std::accumulate(std::begin(fMapLVMotherDaughter_transm[index][LogicVolumeName][IZA_mother]), std::end(fMapLVMotherDaughter_transm[index][LogicVolumeName][IZA_mother]), 0,
00243                                 [](const size_t previous, decltype(*fMapLVMotherDaughter_transm[index][LogicVolumeName][IZA_mother].begin()) p) { return previous+p.second; })
00244                               /2.;
00245 //                               std::cout << "fission " << FissionRatePerN0 << std::endl;
00246         }
00247 
00248         for (std::unordered_map<int, long double>::iterator it2=fMapLVMotherDaughter_transm[index][LogicVolumeName][IZA_mother].begin(); it2!=fMapLVMotherDaughter_transm[index][LogicVolumeName][IZA_mother].end(); ++it2) {
00249             int IZA_daughter = it2->first;
00250             long double ReactionRate_perN0 = it2->second;
00251 
00252             AddMotherAndProgeny(vectorChain, vectorElement, MotherElem, lambda_star, 0., ReactionRate_perN0, IZA_daughter, i, LogicVolumeName, strDecay, FissionRatePerN0);
00253             ++i; // counter of transmutation channels
00254         }
00255     }
00256 }
00257 
00258 // Add the decay channel the mother element and its progeny
00259 void TMXBatemanDecay::AddMotherAndProgeny(std::vector < std::vector < std::vector <long double> > >& vectorChain, std::vector < std::vector <long double> >& vectorElement, TMXElement* MotherElem, long double lambda_star, long double BranchingRatio, long double ReactionRate_perN0, int IZA_daughter, int i, std::string LogicVolumeName, std::string strDecay="", long double FissionRatePerN0=0) {
00260 
00261     std::map<int, TMXElement*> mapStoreElem = TMXElementStore::Store(); // Instance the TMXElement DB
00262 
00263     if(FissionRatePerN0 == 0) {vectorElement.push_back( {(double)MotherElem->IZA(), lambda_star, std::log(BranchingRatio + ReactionRate_perN0) - std::log(lambda_star), 1, 0} );} // Add new element to the vector of elements in case of decay or transmutation
00264     else {vectorElement.push_back( {(double)MotherElem->IZA(), lambda_star, std::log(FissionRatePerN0) - std::log(lambda_star), 1, 0} );} // Add new element to the vector of elements in case of fission
00265     if(i>0) vectorChain.push_back(vectorElement);  // Add new chain of elements in case of several decay paths
00266     else vectorChain[vectorChain.size()-1] = vectorElement;  // Update last chain of elements for the first decay path
00267     int index_VectorElement = vectorChain.size()-1;  // Position of the vector of elements in the vector of chains
00268     int index_element = vectorElement.size()-1;  // Position of the element in the vector of element
00269 
00270     if(strDecay == "SF")  {
00271     // !!! WARNING !!!
00272     // How to manage spontaneous fission ?
00273     // TODO: Rechercher Fission products dans DB GEANT4 pour mme RN avec un neutron en moins (car fission spontanne ici)
00274     // TODO
00275     // Add a hadoc daugther element with the corresponding fission rate and a decay constant = +Infinity (immediate decay)
00276     // Following this element add new chains for each fission product with its probability included in the branching ratio and call this method for each fission product
00277         IZA_daughter = 430990; // Tc-99 at each fission
00278     }
00279     else if(strDecay == "Fission") {
00280 //         std::cout << "FissionRatePerN0 " << FissionRatePerN0 << std::endl;
00281         // Use ReactionRate_perN0 of each fission product as the branching ratio from X to the daugther element
00282         // The sum of branching ratios should be 2, hence the branching ratio is calculated as ReactionRate_perN0/FissionRatePerN0 and FissionRatePerN0 is the sum of ReactionRate_perN0 divided by 2
00283         vectorElement.push_back( {000000, 1E+38, std::log(ReactionRate_perN0) - std::log(FissionRatePerN0), 1, 0} ); // Add new element to the vector of elements
00284         vectorChain[vectorChain.size()-1] = vectorElement;  // Update last chain of elements for the first decay path
00285         index_element = vectorElement.size()-1;  // Update position of the element in the vector of element
00286 
00287         // And continue with the next if
00288     }
00289 
00290     // Manage the decay/transmutation product
00291     if( mapStoreElem[IZA_daughter]) {   // If product found and unstable      -> Add decay         channels to the decay chain
00292                                         // If product found and transmutation -> Add transmutation channels to the decay chain
00293                                         // If product found and stable        -> Add element to the vector of elements and update last chain of elements
00294                                         // If product not found               -> Print an error and continue with the next decay
00295         TMXElement* DaughterElem = mapStoreElem[IZA_daughter];
00296         long double lambda_star_daughter = lambdaStar(DaughterElem, LogicVolumeName);
00297 
00298         if(!DaughterElem->Stable() || lambda_star_daughter != 0) { // if daughter element is unstable or transmuted : implementation of the decay channel
00299             RecursiveChain(DaughterElem, vectorChain, LogicVolumeName, lambda_star_daughter); // Call this method: Construct the chain recursively for the daughter
00300         }
00301         else { // if no decay and no transmutation : Add last (stable) element in the chain
00302             vectorElement.push_back( {(double)DaughterElem->IZA(), 0, 0, 1, 0} ); // Add stable daughter element to the chain of elements only if no transmutation
00303             vectorChain[index_VectorElement] = vectorElement; // Update this chain of elements in the vector of chains of elements
00304             vectorElement.pop_back(); // Remove last (stable) element to the chain of elements
00305         }
00306 
00307         vectorElement.pop_back(); // Remove last element to the chain of elements (because every chain of this element have been implemented
00308 
00309         // Normalization of branching ratios by the number of same linear chain ( normalization of new variable ? -> it should be new variables)
00310         for(int j=index_VectorElement; j < (int)vectorChain.size(); ++j) { vectorChain[j][index_element][3] = vectorChain.size() - index_VectorElement;}  // Number of occurence of this decay path in the list of linear chains
00311     }
00312     else
00313         std::cout << "\n\n\n\n\n\n\n\nDaugther element " << IZA_daughter << " not found in TMXElement database.\n\n\n\n\n\n\n\n" << std::endl;
00314 }
