00316 // Construct linear decay paths of element IZA due to decay or transmutation and update the map of decay paths
00317 void TMXBatemanDecay::ConstructChainNoTransmutation(int IZA) {
00318     if (fMapChains.find(IZA) == fMapChains.end()) { // if element is not already in the map
00319 //         std::cout << "\n *** IZA found : " << IZA << std::endl;
00320 //     if (fMapChains[IZA].size() == 0) { // if element is not already in the map
00321         std::map<int, TMXElement*> mapStoreElem = TMXElementStore::Store();
00322         if( mapStoreElem[IZA]) {
00323             TMXElement* elem = mapStoreElem[IZA];
00324             long double lambda_mother = lambda(elem);
00325 
00326             std::vector < std::vector < std::vector <long double> > > vectorChain;
00327             if(lambda_mother != 0) { // if element is unstable or transmuted : implementation of the decay channel
00328                 std::vector < std::vector <long double> > vectorElement;
00329                 vectorChain.push_back(vectorElement);
00330 
00331                 RecursiveChainNoTransmutation(elem, vectorChain, lambda_mother);  // Construct the chain recursively
00332             }
00333             else { vectorChain.push_back({{(double)IZA, 0, 0, 1, 0}}); } // {IZA, DecayConstant*, ln(BranchingRatio*), Number of occurence, ??}
00334             fMapChains[IZA] = vectorChain;      // Store the vector of chains in a map[key = IZA]
00335         }
00336         else
00337             std::cout << "Element " << IZA << " not found in the database." << std::endl;
00338     }
00339 }
