00341 // Recursive construction of linear chains following each decay path successively
00342 void TMXBatemanDecay::RecursiveChainNoTransmutation(TMXElement* MotherElem, std::vector < std::vector < std::vector <long double> > >& vectorChain, long double lambda_mother) {
00343 
00344     std::vector < std::vector <long double> > vectorElement = vectorChain[vectorChain.size()-1]; // Read the last chain of elements
00345     std::map<int, TMXElement*> mapStoreElem = TMXElementStore::Store(); // Find the new element in the DB
00346     vector<TMXDecayChannel*> vectorDecays = MotherElem->DecayChannels(); // Decay channels of the new element
00347 
00348     // Loop over decay channels of the new element
00349     for( int i=0; i<(int)vectorDecays.size(); ++i ) {
00350         AddMotherAndProgenyNoTransmutation(vectorChain, vectorElement, MotherElem, lambda_mother, vectorDecays[i]->BranchingRatio(), vectorDecays[i]->IZA(), i, vectorDecays[i]->Decay());
00351     }
00352 }
