00354 // Add the decay channel the mother element and its progeny
00355 void TMXBatemanDecay::AddMotherAndProgenyNoTransmutation(std::vector < std::vector < std::vector <long double> > >& vectorChain, std::vector < std::vector <long double> >& vectorElement, TMXElement* MotherElem, long double lambda_mother, long double BranchingRatio, int IZA_daughter, int i, std::string strDecay="") {
00356 
00357     std::map<int, TMXElement*> mapStoreElem = TMXElementStore::Store(); // Instance the TMXElement DB
00358 
00359     vectorElement.push_back( {(double)MotherElem->IZA(), lambda_mother, std::log(BranchingRatio), 1, 0} ); // Add new element to the vector of elements in case of decay or transmutation
00360     if(i>0) vectorChain.push_back(vectorElement);  // Add new chain of elements in case of several decay paths
00361     else vectorChain[vectorChain.size()-1] = vectorElement;  // Update last chain of elements for the first decay path
00362     int index_VectorElement = vectorChain.size()-1;  // Position of the vector of elements in the vector of chains
00363     int index_element = vectorElement.size()-1;  // Position of the element in the vector of element
00364 
00365     if(strDecay == "SF")  {
00366     // !!! WARNING !!!
00367     // How to manage spontaneous fission ?
00368     // TODO: Rechercher Fission products dans DB GEANT4 pour mme RN avec un neutron en moins (car fission spontanne ici)
00369     // TODO
00370     // Add a hadoc daugther element with the corresponding fission rate and a decay constant = +Infinity (immediate decay)
00371     // Following this element add new chains for each fission product with its probability included in the branching ratio and call this method for each fission product
00372         IZA_daughter = 430990; // Tc-99 at each fission
00373     }
00374 
00375     // Manage the decay/transmutation product
00376     if( mapStoreElem[IZA_daughter]) {   // If product found and unstable      -> Add decay         channels to the decay chain
00377                                         // If product found and transmutation -> Add transmutation channels to the decay chain
00378                                         // If product found and stable        -> Add element to the vector of elements and update last chain of elements
00379                                         // If product not found               -> Print an error and continue with the next decay
00380         TMXElement* DaughterElem = mapStoreElem[IZA_daughter];
00381         long double lambda_daughter = lambda(DaughterElem);
00382 
00383         if(!DaughterElem->Stable() || lambda_daughter != 0) { // if daughter element is unstable or transmuted : implementation of the decay channel
00384             RecursiveChainNoTransmutation(DaughterElem, vectorChain, lambda_daughter); // Call this method: Construct the chain recursively for the daughter
00385         }
00386         else { // if no decay and no transmutation : Add last (stable) element in the chain
00387             vectorElement.push_back( {(double)DaughterElem->IZA(), 0, 0, 1, 0} ); // Add stable daughter element to the chain of elements only if no transmutation
00388             vectorChain[index_VectorElement] = vectorElement; // Update this chain of elements in the vector of chains of elements
00389             vectorElement.pop_back(); // Remove last (stable) element to the chain of elements
00390         }
00391 
00392         vectorElement.pop_back(); // Remove last element to the chain of elements (because every chain of this element have been implemented
00393 
00394         // Normalization of branching ratios by the number of same linear chain ( normalization of new variable ? -> it should be new variables)
00395         for(int j=index_VectorElement; j < (int)vectorChain.size(); ++j) { vectorChain[j][index_element][3] = vectorChain.size() - index_VectorElement;}  // Number of occurence of this decay path in the list of linear chains
00396     }
00397     else
00398         std::cout << "\n\n\n\n\n\n\n\nDaugther element " << IZA_daughter << " not found in TMXElement database.\n\n\n\n\n\n\n\n" << std::endl;
00399 }
