00401 // Print stored linear chains for every elements
00402 void TMXBatemanDecay::DumpStoredChains() {
00403     for(auto vectorChain : fMapChains) {
00404         if(vectorChain.second.size() > 0) {
00405             std::cout << "\nElement " << vectorChain.second[0][0][0] << " : " << std::endl;
00406             for(auto vectorElement : vectorChain.second) {
00407                 std::cout << "\n\t\t";
00408                 for(auto element : vectorElement) {
00409                     if(element[1] != 0) {std::cout << (int)element[0] << " --(T=" << 1./element[1] << ", b=" << std::exp(element[2]) << ", n=" << element[3] << ")--> ";}
00410                     else {std::cout << (int)element[0] << " --(T=+Inf, b=" << std::exp(element[2]) << ", n=" << element[3] << ")--> ";}
00411                 }
00412                 std::cout << "the end" << std::endl;
00413             }
00414         }
00415         else
00416             std::cout << "No radioactive element in store" << std::endl;
00417     }
