00484 void TMXBatemanDecay::decayNuclideVector(std::map<int, double>& nuclideVector, std::map<int, int> sourceTerm, double irradiationPeriod, double scalingFactor)
00485 {
00486     bool avoidDecay = false;
00487     std::map<int, double> nuclideVectorUpdated;
00488 
00489     std::map<int, std::vector<long double>> forAccumulator;
00490     for (std::map<int, double>::iterator it=nuclideVector.begin(); it!=nuclideVector.end(); ++it)
00491     {
00492         double IZA_0 = it->first;
00493         long double N0 = it->second;
00494         if (avoidDecay)
00495         {
00496             nuclideVectorUpdated[IZA_0] = nuclideVector[IZA_0];
00497         }
00498         else
00499         {
00500             ConstructChainNoTransmutation(IZA_0);
00501             std::vector < std::vector < std::vector <long double> > > vectorChains = fMapChains[IZA_0];
00502 /*            if(vectorChains[0][0][1] < 2.e-38)
00503             {
00504                 forAccumulator[IZA_0] = {nuclideVector[IZA_0]};
00505             }
00506             else forAccumulator[IZA_0] = {0.};
00507 */
00508             for(auto vectorElem : vectorChains) {
00509                 for(int i=0; i<(int)vectorElem.size(); ++i) { // i = the ith element in the decay chain
00510                     int IZA_i = (int)vectorElem[i][0];
00511                     int m=0;
00512                     long double ln_BranchingRatio_product = ln_branchingProduct(vectorElem, i, m);
00513                     if(forAccumulator.find(IZA_i) == forAccumulator.end()) forAccumulator[IZA_i] = {};
00514                     for(int j=m; j<=i; ++j) {
00515                         long double lambda_j = vectorElem[j][1];
00516                         int sign = 1;
00517                         long double ln_d_ijm = ln_dijm_decay(vectorElem, i, j , m, irradiationPeriod, sign);
00518 
00519                         if (lambda_j > 2.e-38){
00520                             forAccumulator[IZA_i].push_back(sign * std::exp( ln_BranchingRatio_product + ln_d_ijm + std::log(N0) - lambda_j * irradiationPeriod));
00521                             if(std::exp(ln_BranchingRatio_product + ln_d_ijm + std::log(N0) - lambda_j * irradiationPeriod) == 1 && ln_BranchingRatio_product + ln_d_ijm + std::log(N0) - lambda_j * irradiationPeriod != 0)
00522                                 std::cout<< " WARNING, NOT ENOUGH PRECISION NUMBER FOR " << IZA_i<<std::endl;
00523                         }
00524                         else if (lambda_j <= 2.e-38 ) {
00525                             forAccumulator[IZA_i].push_back(std::exp(ln_BranchingRatio_product + std::log(N0)));
00526 
00527 //                            nuclideVectorUpdated[IZA_i] += std::exp(ln_BranchingRatio_product + std::log(N0)) ;
00528 //                            std::cout<< "StableDecay " << IZA_0 << " "<< IZA_i << " "<< std::exp(ln_BranchingRatio_product + std::log(N0)) << " lambda " << lambda_j << " brprod "<<ln_BranchingRatio_product << " dijm " << ln_d_ijm<<std::endl;
00529                         }
00530 /*                        else{
00531                             nuclideVectorUpdated[IZA_i] += sign * std::exp( ln_BranchingRatio_product + ln_d_ijm + std::log(N0) - lambda_j * irradiationPeriod);
00532                             std::cout<< "UnStableDecay " << IZA_0 << " "<< IZA_i << " "<< sign * std::exp( ln_BranchingRatio_product + ln_d_ijm + std::log(N0) - lambda_j * irradiationPeriod) << " lambda " << lambda_j << " brprod "<<ln_BranchingRatio_product<< " dijm " << ln_d_ijm<<std::endl;
00533                             if(std::exp(ln_BranchingRatio_product + ln_d_ijm + std::log(N0) - lambda_j * irradiationPeriod) == 1 && ln_BranchingRatio_product + ln_d_ijm + std::log(N0) - lambda_j * irradiationPeriod != 0)
00534                                 std::cout<< " WARNING, NOT ENOUGH PRECISION NUMBER FOR " << IZA_i<<std::endl;
00535                         }*/
00536                     }
00537                 }
00538             }
00539         }
00540     }
00541 //    std::cout<<std::endl;
00542 //    std::cout << "Decayed initial term" << std::endl;
00543     std::map<int, double> decayedInitialTerm;
00544     for(auto key: forAccumulator)
00545     {
00546         decayedInitialTerm[key.first] = neumaier_sum(key.second);
00547 
00548 //        if(nuclideVector.find(key.first) == nuclideVector.end()) std::cout << key.first << " " << "0" << " -> " << decayedInitialTerm[key.first] << std::endl;
00549 //        else std::cout << key.first << " " << nuclideVector[key.first] << " -> " << decayedInitialTerm[key.first] << std::endl;
00550         nuclideVectorUpdated[key.first] += decayedInitialTerm[key.first];
00551     }
00552 //    std::cout<<std::endl;
00553 
00554     CalculateSourceNtNoTransmutation2(sourceTerm, irradiationPeriod, scalingFactor, nuclideVectorUpdated, avoidDecay);
00555     nuclideVector = std::move(nuclideVectorUpdated);
00556 }
