00587 //                        nuclideVectorUpdated[IZA_i] += sign * std::exp(ln_BranchingRatio_product + ln_d_ijm) * Nelem/irradiationPeriod * (-expm1(-vectorElem[j][1] * irradiationPeriod)) / vectorElem[j][1];
