00593                     {
00594                         forAccumulator[IZA_i].push_back(std::exp(ln_BranchingRatio_product) * Nelem);
00595 //                        nuclideVectorUpdated[IZA_i] += std::exp(ln_BranchingRatio_product) * Nelem ;
00596                     }
