00621 
00622 // https://en.wikipedia.org/wiki/Kahan_summation_algorithm#Further_enhancements
00623 // Note that it doesn't work if -ffast-math is enabled.
00624 long double TMXBatemanDecay::neumaier_sum(std::vector<long double> c)
00625 {
00626     long double sum{};
00627     // A running compensation for lost low-order bits.
00628     long double compensation{};
00629     for (long double i : c)
00630     {
00631         long double temp{ sum + i };
00632         if (std::abs(sum) >= std::abs(i) )
00633         {
00634             // If sum is bigger, low-order digits of input[i] are lost.
00635             compensation += (sum - temp) + i;
00636         }
00637         else {
00638             // Else low-order digits of sum are lost.
00639             compensation += (i - temp) + sum;
00640         }
00641         sum = temp;
00642     }
00643     // Correction only applied once in the very end.
00644     return sum + compensation;
00645 }
00646 
00647 
00648 // Function to implement the Kahan
00649 // summation algorithm
00650 long double TMXBatemanDecay::kahanSum(vector<long double> fa)
00651 {
00652     long double sum = 0.0;
00653 
00654     // Variable to store the error
00655     long double c = 0.0;
00656 
00657     // Loop to iterate over the array
00658     for(long double f : fa)
00659     {
00660         long double y = f - c;
00661         long double t = sum + y;
00662         c = (t - sum) - y;
00663         sum = t;
00664     }
00665     return sum;
00666 }
