00745 void TMXBatemanDecay::CalculateSourceNtNoTransmutation(double t) {
00746     for (std::unordered_map<int, long double>::iterator it_isotope=fMapS_0.begin(); it_isotope!=fMapS_0.end(); ++it_isotope) {
00747         double IZA = it_isotope->first;
00748         long double Nelem = it_isotope->second;
00749 
00750         CheckMotherIsConstant(IZA, Nelem);
00751         // Change the initial isotopic vector of the material with depletion of mother elements
00752 /*        if(fMapN_0[IZA] != 0 && Nelem < 0) { // If the transmuted element (build up or depletion) exists in the isotopic vector of element in the material
00753 //             if(std::abs(Nelem / fMapN_0[IZA]) > 0.01) { std::cout << "!!!! WARNING !!!! The mother element " << IZA << " amount varies more than 1% (" << Nelem / fMapN_0[IZA] *100. << "%)." << std::endl; }
00754 
00755             // Depletion of mother element
00756             if (fMapN_0[IZA] + Nelem > 0) {
00757 //                 if(IZA == 922380) {std::cout << "fMapN_0[IZA] : " << fMapN_0[IZA] << " Nelem : " << Nelem << " result : " << fMapN_0[IZA] + Nelem << std::endl;}   // result = OK
00758                 fMapN_0[IZA] += Nelem; }
00759             else if(fMapN_0[IZA] + Nelem <= 0) {
00760                 fMapN_0.erase(IZA);
00761                 std::cout << "\n!!!! WARNING !!!! Try to consume more quantity of element " << IZA << " than available.\n" << std::endl; // The link between daugther amd mother has been lost, nothing to do in this case
00762             }
00763         }
00764        if(Nelem > 0) { // If build up of a new element*/
00765 //             if(IZA == 922380) { std::cout << "N0 92238 " << Nelem << std::endl;}
00766          ConstructChainNoTransmutation(IZA);  // Construct linear decay paths for every new isotopes in the volume
00767         std::vector < std::vector < std::vector <long double> > > vectorChains = fMapChains[IZA];  // Get the decay chain of this isotope
00768         for(auto vectorElem : vectorChains) {
00769             for(int i=0; i<(int)vectorElem.size(); ++i) { // i = the ith element in the decay chain
00770                 int IZA_i = (int)vectorElem[i][0];
00771 
00772                 // m = contribution of m previous element in the chain to N_i
00773                 int m=0; // only S0 = S[m=0] != 0 because we do the Calculation for every elements in fMapS_0. Index m=0 instead of a for loop with (int m=0; m<=i; ++m)
00774                 long double ln_BranchingRatio_product = ln_branchingProduct(vectorElem, i, m);
00775                 for(int j=m; j<=i; ++j) {
00776                     int sign = 1;
00777                     long double ln_d_ijm = ln_dijm_source(vectorElem, i, j , m, t, sign);
00778                     // If stable element in the chain or not
00779                     if (vectorElem[j][1] <= 2.e-38) {
00780                         fMapNt[IZA_i] += std::exp(ln_BranchingRatio_product) * Nelem ;
00781                     } // if stable, Calculation N_i += ln_BranchingRatio_product * dijm(=1) * ReactionRate*t
00782                     else {
00783 //                             if(IZA_i == 922380) { std::cout << "Src BR:" << std::exp( ln_BranchingRatio_product) << " dijm " << std::exp(ln_dijm_source(vectorElem, i, j , m, t, sign))
00784 //                                 << " S0 " << Nelem/t << " exp " << (1-std::exp(-vectorElem[j][1] * t))/vectorElem[j][1] << " total " << std::exp(ln_BranchingRatio_product + ln_dijm_source(vectorElem, i, j , m, t, sign)) * Nelem/t * (1-std::exp(-vectorElem[j][1] * t))/vectorElem[j][1] << std::endl;}
00785 //                            std::cout<<IZA_i<<" "<<IZA<<" "<<Nelem<<" "<<t<<" "<<vectorElem[j][1]<<std::endl;
00786 //                            std::cout<<IZA << " -> " << IZA_i << " "<<vectorElem[i][1] << " "<<vectorElem[j][1] << " " << std::exp(ln_BranchingRatio_product)<< " "<< std::exp(ln_dijm_source(vectorElem, i, j , m, t, sign))<<std::endl;
00787                         fMapNt[IZA_i] += sign * std::exp(ln_BranchingRatio_product + ln_d_ijm) * Nelem/t * (-expm1(-vectorElem[j][1] * t)) / vectorElem[j][1];
00788                         if(expm1(-vectorElem[j][1] * t) == 0 && vectorElem[j][1] * t != 0)
