00798 void TMXBatemanDecay::CalculateSourceNt(double t, std::string LogicVolumeName) {
00799 //     std::cout << "******** Begin of CalculateSourceNt *************" << std::endl;
00800     for (std::unordered_map<int, std::unordered_map<int, long double>>::iterator it1=fMapLVMotherDaughter_transm[1][LogicVolumeName].begin(); it1!=fMapLVMotherDaughter_transm[1][LogicVolumeName].end(); ++it1) {
00801         double IZA_mother = it1->first;
00802         long double N0_mother = fMapN_0[IZA_mother];
00803 
00804 //         std::cout << "IZA_mother : " << IZA_mother << "\t" << N0_mother << std::endl;
00805         for (std::unordered_map<int,long double>::iterator it2=fMapLVMotherDaughter_transm[1][LogicVolumeName][it1->first].begin(); it2!=fMapLVMotherDaughter_transm[1][LogicVolumeName][it1->first].end(); ++it2) {
00806             double IZA_daughter = it2->first;
00807             long double ReactionRate_perN0 = it2->second;
00808             if(it2->first/10000 > IZA_mother/10000 - 20) { ReactionRate_perN0 = it2->second; }// it2->second = ReactionRate_perN0 = gji * sigma_j * phi
00809             else { ReactionRate_perN0 = it2->second/2.; }  // In case of a fission // it2->second = 2 * ReactionRate_perN0 = 2 * gji * sigma_j * phi because of 2 fission product
00810 
00811             ConstructChain(IZA_daughter, LogicVolumeName);  // Construct linear decay paths for every new isotopes in the volume
00812 //             std::cout << "IZA_daughter : " << IZA_daughter << "\t" << ReactionRate_perN0 << std::endl;
00813 
00814             std::vector < std::vector < std::vector <long double> > > vectorChains = fMapChains[IZA_daughter];
00815             for(auto vectorElem : vectorChains) {
00816                 for(int i=0; i<(int)vectorElem.size(); ++i) { // i = the ith element in the decay chain
00817                     int IZA_i = (int)vectorElem[i][0];
00818                     // m = contribution of m previous element in the chain to N_i
00819                     int m=0; // only S0 = S[m=0] != 0 because we do the Calculation for every elements in fMapLVMotherDaughter_transm. Index m=0 instead of a for loop with (int m=0; m<=i; ++m)
00820                     long double ln_BranchingRatioStar_product = ln_branchingProduct(vectorElem, i, m);
00821                     for(int j=m; j<=i; ++j) {
00822 //       if(IZA_i == 922350) {      std::cout << "IZA_i : " << IZA_i << "; i : " << i << "; j : " << j << " BR_product : " << ln_BranchingRatioStar_product << " vectorElem.size() : " << vectorElem.size() << " d_ijm : " << dijm_source(vectorElem, i, j , m, t) << " N0_mother : " << N0_mother << " T1/2 : " << vectorElem[j][1] << " lambda : " << vectorElem[j][1] << " exp : " << (1-std::exp(-vectorElem[j][1] * t))  << " Add amount : " << ln_BranchingRatioStar_product * dijm_source(vectorElem, i, j , m, t) * ReactionRate_perN0 * N0_mother * (1-std::exp(-vectorElem[j][1] * t))/vectorElem[j][1] << " fMapNt[IZA_i] : " << fMapNt[IZA_i] << std::endl;}
00823                         if (vectorElem[j][1] <= 2.e-38) {fMapNt[IZA_i] += ln_BranchingRatioStar_product * ReactionRate_perN0 * N0_mother ; } // if stable, Calculation N_i += ln_BranchingRatioStar_product * dijm(=1) * ReactionRate_perN0 * N0_mother
00824                         else {fMapNt[IZA_i] += ln_BranchingRatioStar_product * dijm_source(vectorElem, i, j , m, t) * ReactionRate_perN0 * N0_mother * (1-std::exp(-vectorElem[j][1] * t))/vectorElem[j][1]; } // Calculation N_i += ln_BranchingRatioStar_product * dijm * N_0 (1 - exp(- Lambda_j * t))/Lambda_j
00825                     }
00826                 }
00827             }
00828         }
00829     }
00830 //     std::cout << "******** End of CalculateSourceNt *************" << std::endl;
00831 }
