00833 // Multiply every transmuted elements amounts in fMapLVMotherDaughter_transm and fMapLVElem_transm by factor
00834 void TMXBatemanDecay::MultiplyTransmutedElem(long double factor) {
00835     for(int index=0; index<2; ++index){
00836         for (std::unordered_map<std::string, std::unordered_map<int, std::unordered_map<int, long double>>>::iterator it=fMapLVMotherDaughter_transm[index].begin(); it!=fMapLVMotherDaughter_transm[index].end(); ++it) {
00837             for (std::unordered_map<int, std::unordered_map<int,long double>>::iterator it1=fMapLVMotherDaughter_transm[index][it->first].begin(); it1!=fMapLVMotherDaughter_transm[index][it->first].end(); ++it1) {
00838                 for (std::unordered_map<int,long double>::iterator it2=fMapLVMotherDaughter_transm[index][it->first][it1->first].begin(); it2!=fMapLVMotherDaughter_transm[index][it->first][it1->first].end(); ++it2) {
00839                     it2->second = it2->second * factor;
00840 //                    std::cout<< "FIRST "<<it->first<<" "<<it1->first<< " "<< it2->first<< " "<<it2->second<<std::endl;
00841                 }
00842             }
00843         }
00844     }
00845 
00846     for (std::unordered_map<std::string, std::unordered_map<int, long double>>::iterator it=fMapLVElem_transm.begin(); it!=fMapLVElem_transm.end(); ++it) {
00847         for (std::unordered_map<int,long double>::iterator it1=fMapLVElem_transm[it->first].begin(); it1!=fMapLVElem_transm[it->first].end(); ++it1) {
00848             it1->second = it1->second * factor;
00849 //            std::cout<< "SECOND "<<it->first<<" "<<it1->first<<" "<<it1->second<<" "<<std::endl;
00850         }
00851     }
00852 }
