00870 void TMXBatemanDecay::CheckMotherIsConstant(int IZA_mother, long double N_TransmutedElem) {
00871     if(fMapN_0[IZA_mother] > 0) {
00872         if(std::abs(N_TransmutedElem/fMapN_0[IZA_mother]) > 0.01)
00873             std::cout << "Mother element " << IZA_mother << " amount is changed by more than 1% (" << N_TransmutedElem/fMapN_0[IZA_mother]*100. << "%) in this run." << std::endl;
00874     }
00875 }
