00877 // Calculation of d_ijm factor for decay and transmutation term
00878 // In case Lambda*_j = Lambda*_k, we use l'hopital's theorem to calculate the limit
00879 // The limit depends on whether it is the source term or the decay term
00880 long double TMXBatemanDecay::ln_dijm_decay(std::vector < std::vector <long double> >& vectorElem, int i, int j, int m, double t, int& sign) {
00881     long double Lambda_product = 0;
00882     sign = 1;
00883     for(int k=m; k<=i-1; ++k) {Lambda_product += std::log(vectorElem[k][1]);}  // Calculation of the product of lambda* (numerator)
00884     long double DeltaLambda_product = 0;
00885     for(int k=m; k<=i; ++k) {   // Calculation of the product of Delta lambda* (denominator)
00886 //        if(vectorElem[k][1] - vectorElem[j][1] >= 1E-15) {
00887         if(vectorElem[k][1]!=vectorElem[j][1]){
00888             DeltaLambda_product += std::log(std::abs(vectorElem[k][1] - vectorElem[j][1]));
00889             sign *= std::abs(vectorElem[k][1] - vectorElem[j][1]) / (vectorElem[k][1] - vectorElem[j][1]);
00890         }
00891 //             else if(vectorElem[k][1]==vectorElem[j][1] && k!=j) {
00892         else if(std::abs(vectorElem[k][1]-vectorElem[j][1]) < 1E-15 && k!=j) {
00893 //                 std::cout << "\n\n\n L'Hopital's rule ! \n\n\n" << vectorElem[j][0] << " " << vectorElem[k][0] << std::endl;
00894             DeltaLambda_product += std::log(2./t);
00895 //            std::cout<<"HOPITAL"<<std::endl;
00896         }  // To do the Calculation with l'hopital theorem and Lambda_k = Lambda_j + x with x->0
00897  //         }
00898     }
00899 //     if(vectorElem[j][0] == 902340 && vectorElem[i][0] == 822060)
00900 //         std::cout << "\n" << std::endl;
00901 //     if(vectorElem[j][0] == 000000 && m!=i) // In the case of ad hoc element, only the sign matters because it determines if it is a production or a decay.
00902 //         sign = std::pow(-1, i-m-1);
00903 //    std::cout<<DeltaLambda_product<<std::endl;
00904     return (Lambda_product-DeltaLambda_product);
00905 }
00906 
00907 // Calculation of d_ijm factor for source term
00908 // In case Lambda_j = Lambda_k, we use l'hopital's theorem to calculate the limit
00909 // The limit depends on whether it is the source term or the decay term
00910 long double TMXBatemanDecay::ln_dijm_source(std::vector < std::vector <long double> >& vectorElem, int i, int j, int m, double t, int& sign) {
00911     long double Lambda_product = 0;
00912     sign = 1;
00913     for(int k=m; k<=i-1; ++k) {Lambda_product += std::log(vectorElem[k][1]);}  // Calculation of the product of lambda* (numerator)
00914     long double DeltaLambda_product = 0;
00915     for(int k=m; k<=i; ++k) {   // Calculation of the product of Delta lambda* (denominator)
00916         if(vectorElem[k][1]!=vectorElem[j][1]) {
00917             DeltaLambda_product += std::log(std::abs(vectorElem[k][1] - vectorElem[j][1]));
00918             sign *= std::abs(vectorElem[k][1] - vectorElem[j][1]) / (vectorElem[k][1] - vectorElem[j][1]);
00919         }
00920 //         else if(vectorElem[k][1]==vectorElem[j][1] && k!=j) {
00921         else if(std::abs(vectorElem[k][1]-vectorElem[j][1]) < 1E-15 && k!=j) {
00922             DeltaLambda_product += std::log(std::abs(2./(1 - t * std::exp(-vectorElem[j][1] * t) / (1 - std::exp(-vectorElem[j][1] * t)))));
00923             sign *= std::abs(2./(1 - t * std::exp(-vectorElem[j][1] * t) / (1 - std::exp(-vectorElem[j][1] * t)))) / (2./(1 - t * std::exp(-vectorElem[j][1] * t) / (1 - std::exp(-vectorElem[j][1] * t))));
00924         }  // To do the Calculation with l'hopital theorem and Lambda_k = Lambda_j + x with x->0
00925     }
00926 
00927     return (Lambda_product-DeltaLambda_product);
00928 }
00929 
00930 // Calculation of d_ijm factor for source term
00931 // In case Lambda_j = Lambda_k, we use l'hopital's theorem to calculate the limit
00932 // The limit depends on whether it is the source term or the decay term
00933 long double TMXBatemanDecay::dijm_source(std::vector < std::vector <long double> >& vectorElem, int i, int j, int m, double t ) {
00934     long double Lambda_product = 0;
00935     for(int k=m; k<=i-1; ++k) {Lambda_product += std::log(vectorElem[k][1]);}  // Calculation of the product of lambda* (numerator)
00936     long double DeltaLambda_product = 0;
00937     int sign = 1;
00938     for(int k=m; k<=i; ++k) {   // Calculation of the product of Delta lambda* (denominator)
00939         if(vectorElem[k][1]!=vectorElem[j][1]) {
00940             DeltaLambda_product += std::log(std::abs(vectorElem[k][1] - vectorElem[j][1]));
00941             sign *= std::abs(vectorElem[k][1] - vectorElem[j][1]) / (vectorElem[k][1] - vectorElem[j][1]);
00942         }
00943 //         else if(vectorElem[k][1]==vectorElem[j][1] && k!=j) {
00944         else if(std::abs(vectorElem[k][1]-vectorElem[j][1]) < 1E-15 && k!=j) {
00945             DeltaLambda_product += std::log(std::abs(2./(1 - t * std::exp(-vectorElem[j][1] * t) / (1 - std::exp(-vectorElem[j][1] * t)))));
00946             sign *= std::abs(2./(1 - t * std::exp(-vectorElem[j][1] * t) / (1 - std::exp(-vectorElem[j][1] * t)))) / (2./(1 - t * std::exp(-vectorElem[j][1] * t) / (1 - std::exp(-vectorElem[j][1] * t))));
00947         }  // To do the Calculation with l'hopital theorem and Lambda_k = Lambda_j + x with x->0
00948     }
00949 
00950     return sign*std::exp(Lambda_product-DeltaLambda_product);
00951 }
