00953 // Normalization of the transmuted elements amount (in fMapLVMotherDaughter_transm) by the initial amount N0 of the father elements in the material
00954 // Necessary to get sigma*phi instead of sigma*phi*N_i
00955 void TMXBatemanDecay::NormalizeTransmutedElem(int IZA, std::string LogicVolumeName) {
00956     long double N0 = fMapN_0[IZA];
00957     if(N0 != 0) {
00958         for(int index=0; index<2; ++index){
00959             for (std::unordered_map<int,long double>::iterator it1=fMapLVMotherDaughter_transm[index][LogicVolumeName][IZA].begin(); it1!=fMapLVMotherDaughter_transm[index][LogicVolumeName][IZA].end(); ++it1) {
00960 //                     std::cout << "Norm it1->second " << it1->second << " it1->first : " << it1->first << " IZA : " << IZA << " it->second : " << it->second << " it1->second / it->second : " << it1->second / it->second << std::endl;
00961                 it1->second = it1->second / N0;
00962             }
00963         }
00964     }
00965 }
00966 
00967 // Calculation of the decay constant for TMXelement
00968 long double TMXBatemanDecay::lambda(TMXElement* elem) {
00969     if(elem->Tave() < 5.e37) { return 1. / elem->Tave();} // If radioactive element
00970     return 0; // If stable element
00971 }
00972 
00973 // Calculation of the decay constant with transmutation rate for TMXelement
00974 long double TMXBatemanDecay::lambdaStar(TMXElement* elem, std::string LogicVolumeName) {
00975     long double lambdaStar_i = 0;
00976     double IZA_elem = elem->IZA();
00977     std::unordered_map<std::string, std::unordered_map<int, std::unordered_map<int,long double>>> MapLVMotherDaughter_transm[2] = {fMapLVMotherDaughter_transm[0], fMapLVMotherDaughter_transm[1]};
00978     for(int index=0; index<2; ++index){
00979         for (std::unordered_map<int,long double>::iterator it3=MapLVMotherDaughter_transm[index][LogicVolumeName][IZA_elem].begin(); it3!=MapLVMotherDaughter_transm[index][LogicVolumeName][IZA_elem].end(); ++it3) {
00980             lambdaStar_i += (long double)it3->second; // it3->second = ReactionRate_perN0 = gji * sigma_j * phi
00981         }
00982     }
00983 
00984     lambdaStar_i += lambda(elem);
00985     if(lambdaStar_i > 2.e-38) { return lambdaStar_i;} // If decay constant + transmutation rate is significant
00986     return 0; // If stable element
00987 }
00988 
00989 // Calculation of the product of branching ratios for element i from vectorElem
00990 long double TMXBatemanDecay::ln_branchingProduct(std::vector < std::vector <long double> >& vectorElem, int i, int m) {
00991     long double ln_BranchingRatioStar_product = 0;
00992 //     std::cout << std::endl;
00993     for(int l=m; l<=i; ++l) {
00994         ln_BranchingRatioStar_product += vectorElem[l][2];  // Product of branching ratios from 0 to i; Branh[j] gives the branching leading to the next element j+1.
00995 //         std::cout << "l " << l << " i " << i << " vectorElem[l][0] " << vectorElem[l][0] << " bji " << vectorElem[l][2] << " vectorElem[i][3] " << vectorElem[i][3] << " ln_BranchingRatioStar_product " << ln_BranchingRatioStar_product << std::endl;
00996     }
00997 
00998 //     if(vectorElem[i][0] == 922350) {std::cout << "i : " << i << " ln_BranchingRatioStar_product : " << ln_BranchingRatioStar_product << " vectorElem[i][3] : " << vectorElem[i][3] << std::endl;}
00999     return (ln_BranchingRatioStar_product - std::log(vectorElem[i][3]));  // The branching product is divided by the number of occurence of this particular chain with element i in the list of all decay chains of element m (the calculation will be repeated N times)
01000 //     return ln_BranchingRatioStar_product;
01001 }
