01005 // Substract the transmuted elements to N0 number of mother atoms and add the daugther to N0 map of elements for decay
01006 void TMXBatemanDecay::SumTransmutedElements(std::string LogicVolumeName) {
01007     for(int index=0; index<2; ++index){
01008         for (std::unordered_map<int, std::unordered_map<int,long double>>::iterator it_mother=fMapLVMotherDaughter_transm[index][LogicVolumeName].begin(); it_mother!=fMapLVMotherDaughter_transm[index][LogicVolumeName].end(); ++it_mother) {
01009             for (std::unordered_map<int,long double>::iterator it_daughter=fMapLVMotherDaughter_transm[index][LogicVolumeName][it_mother->first].begin(); it_daughter!=fMapLVMotherDaughter_transm[index][LogicVolumeName][it_mother->first].end(); ++it_daughter) {
01010 //                 if(/*it_mother->first == 922380 &&*/ LogicVolumeName == "lFuel2" && it_daughter->second>1E10) {std::cout << "Volume: " << LogicVolumeName << " mother: " << it_mother->first << " daughter: " << it_daughter->first << "results: " << it_daughter->second/(index+1.) << std::endl;}
01011                 // Sum of element production (negative for mother and positive for daughter)
01012 //                std::cout<<it_mother  ->first << " "<<fMapS_0[it_mother  ->first] <<std::endl;
01013 //                std::cout<<it_daughter->first << " "<<fMapS_0[it_daughter->first] <<std::endl;
01014 /*                if(LogicVolumeName == "lfuelmat23")
01015                 {
01016                     std::cout<<"Prod in "<<it_mother  ->first << " " << fMapS_0[it_mother  ->first]  << " -> " << it_daughter  ->first << " "<<fMapS_0[it_daughter  ->first] <<std::endl;
01017                 }
01018 */                fMapS_0[it_mother  ->first] -= it_daughter->second/(index+1.);//*100; // index = 0 -> 1 reaction product | index = 1 -> 2 reaction products
01019                 fMapS_0[it_daughter->first] += it_daughter->second;//*100;
